#include <stdio.h>
#include <unistd.h>
#include <sys/types.h>
#include <stddef.h>

void OS_BSP_ConsoleOutput_Impl(const char *Str, size_t DataLen)
{
    ssize_t WriteLen;

    /* Log entry state */
    {
        FILE *log = fopen("/tmp/logs/47.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "ENTRY: OS_BSP_ConsoleOutput_Impl called. Str=%p, DataLen=%zu\n", (void *)Str, DataLen);
            fclose(log);
        }
    }

    while (DataLen > 0)
    {
        /* writes the raw data directly to STDOUT_FILENO (unbuffered) */
        WriteLen = write(STDOUT_FILENO, Str, DataLen);

        /* Log result of write and current state before any pointer modification */
        {
            FILE *log = fopen("/tmp/logs/47.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "AFTER write(): WriteLen=%zd, Str(before)=%p, DataLen(before)=%zu\n",
                        WriteLen, (void *)Str, DataLen);
                fclose(log);
            }
        }

        if (WriteLen <= 0)
        {
            /* no recourse if this fails, just stop. */
            FILE *log = fopen("/tmp/logs/47.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "BREAK: write returned %zd, stopping output. Str=%p, DataLen=%zu\n",
                        WriteLen, (void *)Str, DataLen);
                fclose(log);
            }
            break;
        }

        /* Capture pointer before modification for logging evidence */
        {
            const char *oldStr = Str;

            /* Violation scenario: modifying the pointer parameter itself */
            Str += WriteLen;
            DataLen -= WriteLen;

            FILE *log = fopen("/tmp/logs/47.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "MODIFIED POINTER: old Str=%p -> new Str=%p, advanced by=%td, DataLen(after)=%zu\n",
                        (void *)oldStr, (void *)Str, (ptrdiff_t)(Str - oldStr), DataLen);
                fclose(log);
            }
        }
    }

    /* Log exit */
    {
        FILE *log = fopen("/tmp/logs/47.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "EXIT: OS_BSP_ConsoleOutput_Impl finished. Final Str=%p, DataLen=%zu\n", (void *)Str, DataLen);
            fclose(log);
        }
    }
}
