#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <pthread.h>
#include <unistd.h>

/* Probe helper: append diagnostic information to /tmp/logs/12.txt */
static void OS_BSP_LogProbe(const char *label, int status)
{
    FILE *log = fopen("/tmp/logs/12.txt", "a");
    if (log != NULL)
    {
        /* Record the label, numeric status, and strerror() output for reproducibility */
        fprintf(log, "%s: status=%d, strerror=%s\n", label, status, strerror(status));
        fclose(log);
    }
}

void OS_BSP_Initialize(void)
{
    FILE *              fp;
    char                buffer[32];
    pthread_mutexattr_t mutex_attr;
    int                 status;

    /*
     * If not running as root, check /proc/sys/fs/mqueue/msg_max
     *
     * This special file represents the max depth of a POSIX message queue for an unprivileged user.
     *
     * In order to facilitate running in simulation mode without any need for root access --
     * this will allow the OSAL to successfully create message queues by truncating anything larger than this size.
     *
     * No need to check _LINUX_OS_ here; if the file fails to open, i.e. if not on Linux and the file does not exist,
     * then leave well enough alone and don't do anything.
     */
    if (geteuid() != 0)
    {
        fp = fopen("/proc/sys/fs/mqueue/msg_max", "r");
        if (fp)
        {
            if (fgets(buffer, sizeof(buffer), fp) != NULL)
            {
                OS_BSP_Global.MaxQueueDepth = OSAL_BLOCKCOUNT_C(strtoul(buffer, NULL, 10));
                BSP_DEBUG("Maximum user msg queue depth = %u\n", (unsigned int)OS_BSP_Global.MaxQueueDepth);
            }
            fclose(fp);
        }
    }

    /* Initialize the low level access mutex (w/priority inheritance) */
    status = pthread_mutexattr_init(&mutex_attr);
    /* Log the returned status and descriptive string so testers can observe that pthread returns 0 on success */
    OS_BSP_LogProbe("pthread_mutexattr_init returned", status);
    if (status < 0)
    {
        BSP_DEBUG("pthread_mutexattr_init: %s\n", strerror(status));
        OS_BSP_LogProbe("pthread_mutexattr_init: entered if (status < 0)", status);
    }
    else
    {
        /* Also log when the branch is not taken to provide explicit evidence that the if-body is never executed for normal pthread results */
        OS_BSP_LogProbe("pthread_mutexattr_init: did NOT enter if (status < 0)", status);
    }

    status = pthread_mutexattr_setprotocol(&mutex_attr, PTHREAD_PRIO_INHERIT);
    OS_BSP_LogProbe("pthread_mutexattr_setprotocol returned", status);
    if (status < 0)
    {
        BSP_DEBUG("pthread_mutexattr_setprotocol: %s\n", strerror(status));
        OS_BSP_LogProbe("pthread_mutexattr_setprotocol: entered if (status < 0)", status);
    }
    else
    {
        OS_BSP_LogProbe("pthread_mutexattr_setprotocol: did NOT enter if (status < 0)", status);
    }

    status = pthread_mutex_init(&OS_BSP_GenericLinuxGlobal.AccessMutex, &mutex_attr);
    OS_BSP_LogProbe("pthread_mutex_init returned", status);
    if (status < 0)
    {
        BSP_DEBUG("pthread_mutex_init: %s\n", strerror(status));
        OS_BSP_LogProbe("pthread_mutex_init: entered if (status < 0)", status);
    }
    else
    {
        OS_BSP_LogProbe("pthread_mutex_init: did NOT enter if (status < 0)", status);
    }
}
