void OS_BSP_Initialize(void)
{
    FILE *              fp;
    FILE *              logfp;
    char                buffer[32];
    pthread_mutexattr_t mutex_attr;
    int                 status;

    /* Open probe log file early so we can record control flow/state */
    logfp = fopen("/tmp/logs/19.txt", "a");
    if (logfp)
    {
        fprintf(logfp, "ENTER: OS_BSP_Initialize()\n");
    }

    /*
     * If not running as root, check /proc/sys/fs/mqueue/msg_max
     *
     * This special file represents the max depth of a POSIX message queue for an unprivileged user.
     *
     * In order to facilitate running in simulation mode without any need for root access --
     * this will allow the OSAL to successfully create message queues by truncating anything larger than this size.
     *
     * No need to check _LINUX_OS_ here; if the file fails to open, i.e. if not on Linux and the file does not exist,
     * then leave well enough alone and don't do anything.
     */
    if (geteuid() != 0)
    {
        if (logfp)
        {
            fprintf(logfp, "PATH: not root (geteuid() != 0). Will try to read /proc/sys/fs/mqueue/msg_max\n");
        }

        fp = fopen("/proc/sys/fs/mqueue/msg_max", "r");
        if (fp)
        {
            if (fgets(buffer, sizeof(buffer), fp) != NULL)
            {
                if (logfp)
                {
                    /* Record raw buffer content read from proc file */
                    fprintf(logfp, "READ: /proc/sys/fs/mqueue/msg_max => '%s'\n", buffer);
                }

                OS_BSP_Global.MaxQueueDepth = OSAL_BLOCKCOUNT_C(strtoul(buffer, NULL, 10));
                BSP_DEBUG("Maximum user msg queue depth = %u\n", (unsigned int)OS_BSP_Global.MaxQueueDepth);

                if (logfp)
                {
                    fprintf(logfp, "STATE: OS_BSP_Global.MaxQueueDepth = %u\n", (unsigned int)OS_BSP_Global.MaxQueueDepth);
                }
            }
            else
            {
                if (logfp)
                {
                    fprintf(logfp, "READ: fgets returned NULL when reading /proc/sys/fs/mqueue/msg_max\n");
                }
            }
            fclose(fp);
            fp = NULL;
        }
        else
        {
            if (logfp)
            {
                fprintf(logfp, "OPEN: failed to open /proc/sys/fs/mqueue/msg_max (errno=%d)\n", errno);
            }
        }
    }
    else
    {
        if (logfp)
        {
            fprintf(logfp, "PATH: running as root (geteuid() == 0). Skipping /proc/sys/fs/mqueue/msg_max check\n");
        }
    }

    /* Initialize the low level access mutex (w/priority inheritance) */
    if (logfp)
    {
        fprintf(logfp, "CALL: pthread_mutexattr_init()\n");
    }
    status = pthread_mutexattr_init(&mutex_attr);
    if (logfp)
    {
        fprintf(logfp, "RET: pthread_mutexattr_init -> status=%d\n", status);
    }
    if (status < 0)
    {
        if (logfp)
        {
            /* Important: do not call strerror(status) here in the probe; log the raw status value to avoid introducing new string handling issues */
            fprintf(logfp, "ERROR: pthread_mutexattr_init returned negative status=%d; original code will call strerror(status) next\n", status);
        }
        BSP_DEBUG("pthread_mutexattr_init: %s\n", strerror(status));
    }

    if (logfp)
    {
        fprintf(logfp, "CALL: pthread_mutexattr_setprotocol(PTHREAD_PRIO_INHERIT)\n");
    }
    status = pthread_mutexattr_setprotocol(&mutex_attr, PTHREAD_PRIO_INHERIT);
    if (logfp)
    {
        fprintf(logfp, "RET: pthread_mutexattr_setprotocol -> status=%d\n", status);
    }
    if (status < 0)
    {
        if (logfp)
        {
            fprintf(logfp, "ERROR: pthread_mutexattr_setprotocol returned negative status=%d; original code will call strerror(status) next\n", status);
        }
        BSP_DEBUG("pthread_mutexattr_setprotocol: %s\n", strerror(status));
    }

    if (logfp)
    {
        fprintf(logfp, "CALL: pthread_mutex_init(&OS_BSP_GenericLinuxGlobal.AccessMutex, &mutex_attr)\n");
    }
    status = pthread_mutex_init(&OS_BSP_GenericLinuxGlobal.AccessMutex, &mutex_attr);
    if (logfp)
    {
        fprintf(logfp, "RET: pthread_mutex_init -> status=%d\n", status);
    }
    if (status < 0)
    {
        if (logfp)
        {
            fprintf(logfp, "ERROR: pthread_mutex_init returned negative status=%d; original code will call strerror(status) next\n", status);
        }
        BSP_DEBUG("pthread_mutex_init: %s\n", strerror(status));
    }

    if (logfp)
    {
        fprintf(logfp, "EXIT: OS_BSP_Initialize()\n");
        fclose(logfp);
    }
}
