#include <stdio.h>
#include <string.h>

int32 OS_CloseFileByName(const char *Filename)
{
    int32                        return_code;
    int32                        close_code;
    OS_object_iter_t             iter;
    OS_stream_internal_record_t *stream;
    FILE                       *logf = NULL;

    /* Check parameters */
    OS_CHECK_POINTER(Filename);

    /* Open a log for tracing control flow and state for the violation scenario */
    logf = fopen("/tmp/logs/1583.txt", "a");
    if (logf)
    {
        fprintf(logf, "OS_CloseFileByName called with Filename=\"%s\"\n", Filename);
    }

    return_code = OS_FS_ERR_PATH_INVALID;

    OS_ObjectIdIterateActive(LOCAL_OBJID_TYPE, &iter);
    if (logf)
    {
        fprintf(logf, "Iterator initialized\n");
    }

    while (OS_ObjectIdIteratorGetNext(&iter))
    {
        stream = OS_OBJECT_TABLE_GET(OS_stream_table, iter.token);

        if (logf)
        {
            fprintf(logf, "Iter token=%lu, stream_ptr=%p\n", (unsigned long)iter.token, (void *)stream);
            if (stream)
            {
                fprintf(logf, "stream->socket_domain=%d, stream->stream_name=\"%s\"\n",
                        (int)stream->socket_domain,
                        stream->stream_name ? stream->stream_name : "(null)");
            }
            else
            {
                fprintf(logf, "stream is NULL\n");
            }
        }

        if (stream->socket_domain == OS_SocketDomain_INVALID && (strcmp(stream->stream_name, Filename) == 0))
        {
            if (logf)
            {
                fprintf(logf, "Match found: invoking OS_ObjectIdIteratorProcessEntry for token=%lu\n", (unsigned long)iter.token);
            }

            /* call OS_close() on the entry referred to by the iterator */
            close_code = OS_ObjectIdIteratorProcessEntry(&iter, OS_FileIteratorClose);

            if (logf)
            {
                fprintf(logf, "OS_ObjectIdIteratorProcessEntry returned close_code=%d\n", (int)close_code);
            }

            if (return_code == OS_FS_ERR_PATH_INVALID || close_code != OS_SUCCESS)
            {
                if (logf)
                {
                    fprintf(logf, "Updating return_code from %d to %d\n", (int)return_code, (int)close_code);
                }
                return_code = close_code;
            }
        }
        else
        {
            if (logf && stream)
            {
                int cmp = strcmp(stream->stream_name ? stream->stream_name : "", Filename);
                fprintf(logf, "No match for token=%lu: socket_domain=%d, strcmp=%d\n",
                        (unsigned long)iter.token, (int)stream->socket_domain, cmp);
            }
        }
    }

    OS_ObjectIdIteratorDestroy(&iter);

    if (logf)
    {
        fprintf(logf, "Iterator destroyed, final return_code=%d\n", (int)return_code);
        fclose(logf);
    }

    return return_code;
}
