#include <stdio.h>
#include <stdarg.h>
#include <string.h>

/* Helper logging probe: append human-readable trace to /tmp/logs/2262.txt */
static void OS_Console_Log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/2262.txt", "a");
    if (!f)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);

    fclose(f);
}

int32 OS_ConsoleWrite(osal_id_t console_id, const char *Str)
{
    int32                         return_code;
    OS_object_token_t             token;
    OS_console_internal_record_t *console;
    size_t                        PendingWritePos;

    OS_Console_Log("OS_ConsoleWrite ENTRY: console_id=%lu, Str_ptr=%p", (unsigned long)console_id, (void *)Str);

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_OBJECT_TYPE_OS_CONSOLE, console_id, &token);
    OS_Console_Log("OS_ObjectIdGetById -> return_code=%d", return_code);

    if (return_code == OS_SUCCESS)
    {
        console = OS_OBJECT_TABLE_GET(OS_console_table, token);

        /*
         * The entire string should be put to the ring buffer,
         * or none of it.  Therefore the WritePos in the table
         * is not updated until complete success.
         */
        PendingWritePos = console->WritePos;

        OS_Console_Log("console=%p WritePos=%zu PendingWritePos=%zu device_name='%s'", (void *)console, console->WritePos, PendingWritePos, console->device_name);

        if (console->device_name[0] != 0)
        {
            OS_Console_Log("Calling OS_Console_CopyOut for device_name (before): PendingWritePos=%zu device_name='%s'", PendingWritePos, console->device_name);
            return_code = OS_Console_CopyOut(console, console->device_name, &PendingWritePos);
            OS_Console_Log("Returned from OS_Console_CopyOut (device): return_code=%d PendingWritePos=%zu", return_code, PendingWritePos);
        }
        if (return_code == OS_SUCCESS)
        {
            OS_Console_Log("Calling OS_Console_CopyOut for Str (before): PendingWritePos=%zu Str_ptr=%p", PendingWritePos, (void *)Str);
            if (Str != NULL)
            {
                /* log a small prefix of the string for diagnostics */
                size_t len = strnlen(Str, 64);
                char prefix[65];
                memcpy(prefix, Str, len);
                prefix[len] = '\0';
                OS_Console_Log("Str prefix: '%s' (len=%zu)", prefix, len);
            }
            else
            {
                OS_Console_Log("Str is NULL");
            }

            return_code = OS_Console_CopyOut(console, Str, &PendingWritePos);
            OS_Console_Log("Returned from OS_Console_CopyOut (Str): return_code=%d PendingWritePos=%zu", return_code, PendingWritePos);
        }

        if (return_code == OS_SUCCESS)
        {
            /* the entire message was successfully written */
            console->WritePos = PendingWritePos;
            OS_Console_Log("Write successful: new WritePos=%zu", console->WritePos);
        }
        else
        {
            /* the message did not fit */
            ++console->OverflowEvents;
            OS_Console_Log("Write failed: return_code=%d OverflowEvents=%u", return_code, (unsigned)console->OverflowEvents);
        }

        /*
         * Notify the underlying console implementation of new data.
         * This will forward the data to the actual console device.
         *
         * This is done while still locked, so it can support
         * either a synchronous or asynchronous implementation.
         */
        if (console->IsAsync)
        {
            /* post the sem for the utility task to run */
            OS_Console_Log("Console is async -> calling OS_ConsoleWakeup_Impl");
            OS_ConsoleWakeup_Impl(&token);
        }
        else
        {
            /* output directly */
            OS_Console_Log("Console is sync -> calling OS_ConsoleOutput_Impl");
            OS_ConsoleOutput_Impl(&token);
        }

        OS_ObjectIdRelease(&token);
        OS_Console_Log("Released token and exiting critical section");
    }
    else
    {
        OS_Console_Log("OS_ObjectIdGetById failed, skipping console write, return_code=%d", return_code);
    }

    OS_Console_Log("OS_ConsoleWrite EXIT: return_code=%d", return_code);

    return return_code;
}
