#include <stdio.h>
#include <stdarg.h>
#include <stddef.h>

/* Probe logging helper: append to /tmp/logs/804.txt */
static void OS_FdSet_Logf(const char *fmt, ...)
{
    va_list args;
    FILE *f = fopen("/tmp/logs/804.txt", "a");
    if (f == NULL)
    {
        return;
    }
    va_start(args, fmt);
    vfprintf(f, fmt, args);
    va_end(args);
    fclose(f);
}

static void OS_FdSet_ConvertOut_Impl(fd_set *OS_set, OS_FdSet *OSAL_set)
{
    size_t       offset;
    size_t       bit;
    osal_index_t id;
    uint8        objids;
    int          osfd;
    size_t       arr_size = sizeof(OSAL_set->object_ids);

    /* Log entry and array size used in the for-loop second clause */
    OS_FdSet_Logf("OS_FdSet_ConvertOut_Impl entry: object_ids size = %zu\n", arr_size);

    for (offset = 0; offset < sizeof(OSAL_set->object_ids); ++offset)
    {
        /* Log beginning of iteration for offset */
        OS_FdSet_Logf("for-loop offset start: offset=%zu, element_before=0x%02x\n",
                      offset, (unsigned)OSAL_set->object_ids[offset]);

        objids = OSAL_set->object_ids[offset];
        bit    = 0;
        while (objids != 0)
        {
            id = OSAL_INDEX_C((offset * 8) + bit);
            OS_FdSet_Logf("  while-loop: offset=%zu, bit=%zu, objids=0x%02x, id=%u\n",
                          offset, bit, (unsigned)objids, (unsigned)id);

            if ((objids & 0x01) != 0 && id < OS_MAX_NUM_OPEN_FILES)
            {
                osfd = OS_impl_filehandle_table[id].fd;
                OS_FdSet_Logf("    candidate set bit: id=%u, osfd=%d\n", (unsigned)id, osfd);

                if (osfd < 0 || !FD_ISSET(osfd, OS_set))
                {
                    /* Log value before modification */
                    OS_FdSet_Logf("    modifying: offset=%zu, bit=%zu, before=0x%02x\n",
                                  offset, bit, (unsigned)OSAL_set->object_ids[offset]);

                    OSAL_set->object_ids[offset] &= ~(1 << bit);

                    /* Log value after modification */
                    OS_FdSet_Logf("    modified: offset=%zu, bit=%zu, after=0x%02x\n",
                                  offset, bit, (unsigned)OSAL_set->object_ids[offset]);
                }
                else
                {
                    OS_FdSet_Logf("    kept: id=%u is set in OS_set (osfd=%d)\n", (unsigned)id, osfd);
                }
            }

            ++bit;
            objids >>= 1;
        }

        /* Log end of iteration for offset */
        OS_FdSet_Logf("for-loop offset end: offset=%zu, element_after=0x%02x\n",
                      offset, (unsigned)OSAL_set->object_ids[offset]);
    }

    OS_FdSet_Logf("OS_FdSet_ConvertOut_Impl exit\n");
}
