#include <stdio.h>
#include <string.h>
#include <stddef.h>
#include <stdbool.h>

bool OS_FileSys_FindVirtMountPoint(void *ref, const OS_object_token_t *token, const OS_common_record_t *obj)
{
    FILE *log = fopen("/tmp/logs/1753.txt", "a");
    if (log)
    {
        fprintf(log, "ENTRY: OS_FileSys_FindVirtMountPoint ref=%p token=%p obj=%p\n", ref, token, obj);
    }

    OS_filesys_internal_record_t *filesys;
    const char *                  target = (const char *)ref;
    size_t                        mplen;

    filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, *token);

    if (log)
    {
        /* flags is printed as an unsigned int for debugging purposes */
        fprintf(log, "filesys=%p flags=0x%08x\n", (void *)filesys, (unsigned int)filesys->flags);
    }

    if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL) == 0)
    {
        if (log)
        {
            fprintf(log, "EXIT: not a virtual mount (flags & IS_MOUNTED_VIRTUAL == 0) -> false\n");
            fclose(log);
        }
        return false;
    }

    mplen = OS_strnlen(filesys->virtual_mountpt, sizeof(filesys->virtual_mountpt));

    if (log)
    {
        /* Log the computed mountpoint length and a truncated copy of the mountpoint */
        size_t to_print = (mplen < sizeof(filesys->virtual_mountpt)) ? mplen : sizeof(filesys->virtual_mountpt) - 1;
        fprintf(log, "mplen=%zu virtual_mountpt=(first %zu bytes): \"%.*s\" target=%p\n",
                mplen, to_print, (int)to_print, filesys->virtual_mountpt, (void *)target);
        fprintf(log, "target_preview=\"%s\"\n", target ? target : "(null)");
    }

    /*
     * The virtual_mountpt member should be a substring of the search target.
     * If this matches a basic substring check then it may be match
     */
    if (mplen == 0 || mplen >= sizeof(filesys->virtual_mountpt) ||
        strncmp(target, filesys->virtual_mountpt, mplen) != 0)
    {
        /* not a substring, so not a match */
        if (log)
        {
            fprintf(log, "EXIT: substring check failed (mplen==0 || mplen>=size || strncmp!=0) -> false\n");
            fclose(log);
        }
        return false;
    }

    /*
     * Confirm that the substring ends at either a directory separator
     * or the end of string  (so exact mount points also match).
     *
     * For instance consider a virtual_mountpt of /mnt/abc and searching
     * for target=/mnt/abcd - this should return false in that case.
     */
    bool result = (target[mplen] == '/' || target[mplen] == 0);

    if (log)
    {
        int ch = target ? (int)(unsigned char)target[mplen] : -1;
        fprintf(log, "final_char_at_index_%zu=0x%02x (%c) result=%d\n",
                mplen, ch, (ch >= 32 && ch < 127) ? (char)ch : '?', result);
        fprintf(log, "EXIT: returning %d\n", result);
        fclose(log);
    }

    return result;
}
