#include <stdio.h>
#include <stdarg.h>
#include <stddef.h>
#include <string.h>
#include <stdint.h>

/* Helper logging probe: append human-readable trace information to /tmp/logs/1763.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/1763.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list args;
    va_start(args, fmt);
    vfprintf(f, fmt, args);
    fprintf(f, "\n");
    va_end(args);

    fclose(f);
}

int32 OS_FileSys_Initialize(char *address, const char *fsdevname, const char *fsvolname, size_t blocksize,
                            osal_blockcount_t numblocks, bool should_format)
{
    OS_filesys_internal_record_t *filesys;
    int32                         return_code;
    OS_object_token_t             token;

    /* Log entry and parameters (address is the pointer under scrutiny) */
    probe_log("OS_FileSys_Initialize ENTRY: address=%p, fsdevname='%s', fsvolname='%s', blocksize=%zu, numblocks=%llu, should_format=%d",
              (const void *)address,
              (fsdevname != NULL) ? fsdevname : "(null)",
              (fsvolname != NULL) ? fsvolname : "(null)",
              blocksize,
              (unsigned long long)numblocks,
              (int)should_format);

    /*
     * Check parameters
     *
     * Note "address" is not checked, because in certain configurations it can be validly null.
     */
    OS_CHECK_STRING(fsdevname, sizeof(filesys->device_name), OS_FS_ERR_PATH_TOO_LONG);
    OS_CHECK_STRING(fsvolname, sizeof(filesys->volume_name), OS_FS_ERR_PATH_TOO_LONG);

    /* check names are not empty strings */
    if (fsdevname[0] == 0 || fsvolname[0] == 0)
    {
        probe_log("OS_FileSys_Initialize: Invalid path (empty string) for fsdevname or fsvolname");
        return OS_FS_ERR_PATH_INVALID;
    }

    return_code = OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, fsdevname, &token);
    probe_log("OS_ObjectIdAllocateNew returned %d", (int)return_code);

    if (return_code == OS_SUCCESS)
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        probe_log("Allocated filesys record at %p (token=%p)", (void *)filesys, (void *)&token);

        /* Reset the table entry and save the name */
        OS_OBJECT_INIT(token, filesys, device_name, fsdevname);

        /* populate the VolumeName and BlockSize ahead of the Impl call,
         * so the implementation can reference this info if necessary */
        filesys->blocksize = blocksize;
        filesys->numblocks = numblocks;

        /* Log state before assigning address */
        probe_log("Before assign: filesys->address=%p, incoming address=%p", (const void *)filesys->address, (const void *)address);

        filesys->address   = address;

        /* Log state after assigning address */
        probe_log("After assign: filesys->address=%p", (const void *)filesys->address);

        strncpy(filesys->volume_name, fsvolname, sizeof(filesys->volume_name) - 1);
        filesys->volume_name[sizeof(filesys->volume_name) - 1] = '\0';

        probe_log("filesys state: blocksize=%zu, numblocks=%llu, volume_name='%s'", filesys->blocksize,
                  (unsigned long long)filesys->numblocks, filesys->volume_name);

        /*
         * Determine basic type of filesystem, if not already known
         *
         * if either an address was supplied, or if the volume name
         * contains the string "RAM" then it is a RAM disk. Otherwise
         * leave the type as UNKNOWN and let the implementation decide.
         */
        if (filesys->fstype == OS_FILESYS_TYPE_UNKNOWN &&
            (filesys->address != NULL || strncmp(filesys->volume_name, OS_FILESYS_RAMDISK_VOLNAME_PREFIX,
                                                 sizeof(OS_FILESYS_RAMDISK_VOLNAME_PREFIX) - 1) == 0))
        {
            filesys->fstype = OS_FILESYS_TYPE_VOLATILE_DISK;
            probe_log("filesys fstype set to VOLATILE_DISK (address=%p, volume_name='%s')", (const void *)filesys->address, filesys->volume_name);
        }
        else
        {
            probe_log("filesys fstype left as %d", (int)filesys->fstype);
        }

        return_code = OS_FileSysStartVolume_Impl(&token);
        probe_log("OS_FileSysStartVolume_Impl returned %d", (int)return_code);

        if (return_code == OS_SUCCESS)
        {
            /*
             * The "mkfs" call also formats the device.
             * this is the primary difference between mkfs and initfs.
             */
            if (should_format)
            {
                probe_log("Formatting requested for filesys at %p (volume='%s')", (const void *)filesys, filesys->volume_name);
                return_code = OS_FileSysFormatVolume_Impl(&token);
                probe_log("OS_FileSysFormatVolume_Impl returned %d", (int)return_code);
            }

            if (return_code == OS_SUCCESS)
            {
                filesys->flags |= OS_FILESYS_FLAG_IS_READY;
                probe_log("filesys marked READY (flags=0x%08x)", (unsigned int)filesys->flags);
            }
            else
            {
                /*
                 * To avoid leaving in an intermediate state,
                 * this also stops the volume if formatting failed.
                 * Cast to void to repress analysis warnings for
                 * ignored return value.
                 */
                probe_log("Formatting failed, stopping volume (filesys=%p)", (const void *)filesys);
                (void)OS_FileSysStopVolume_Impl(&token);
            }
        }

        /* Check result, finalize record, and unlock global table. */
        return_code = OS_ObjectIdFinalizeNew(return_code, &token, NULL);
        probe_log("OS_ObjectIdFinalizeNew returned %d; final return_code=%d", (int)return_code, (int)return_code);
    }

    probe_log("OS_FileSys_Initialize EXIT: return_code=%d", (int)return_code);

    return return_code;
}
