#include <time.h>

/*
 * Probe logging helper. Appends human-readable messages to /tmp/logs/1923.txt
 * Keeps logging side-effects minimal (open/append/close per message).
 */
static void log_probe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/1923.txt", "a");
    if (f == NULL)
    {
        return;
    }

    /* Timestamp each entry for easier tracing */
    time_t t = time(NULL);
    struct tm tmv;
    if (localtime_r(&t, &tmv) != NULL)
    {
        char buf[64];
        if (strftime(buf, sizeof(buf), "%Y-%m-%d %H:%M:%S", &tmv) > 0)
        {
            fprintf(f, "%s - ", buf);
        }
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fflush(f);
    fclose(f);
}

