/* Probe helpers to collect control-flow/state information for debugging. */
static void log_probe(const char *fmt, ...)
{
    va_list args;
    FILE *fp = fopen("/tmp/logs/2423.txt", "a");
    if (fp == NULL)
    {
        return;
    }

    va_start(args, fmt);
    vfprintf(fp, fmt, args);
    va_end(args);
    fputc('\n', fp);
    fclose(fp);
}

static void log_bytes(const void *ptr, size_t len)
{
    if (ptr == NULL)
    {
        log_probe("log_bytes: ptr=NULL");
        return;
    }

    const unsigned char *p = (const unsigned char *)ptr;
    FILE *fp = fopen("/tmp/logs/2423.txt", "a");
    if (fp == NULL)
    {
        return;
    }

    for (size_t i = 0; i < len; ++i)
    {
        fprintf(fp, "%02x", p[i]);
        if (((i + 1) % 16) == 0)
            fputc('\n', fp);
        else
            fputc(' ', fp);
    }
    fputc('\n', fp);
    fclose(fp);
}

