void OS_CreateSocketName(const OS_object_token_t *token, const OS_SockAddr_t *Addr, const char *parent_name)
{
    size_t                       len;
    uint16                       port;
    OS_stream_internal_record_t *sock;

    sock = OS_OBJECT_TABLE_GET(OS_stream_table, *token);

    if (OS_SocketAddrToString_Impl(sock->stream_name, sizeof(sock->stream_name), Addr) != OS_SUCCESS)
    {
        sock->stream_name[0] = 0;
    }
    if (OS_SocketAddrGetPort_Impl(&port, Addr) == OS_SUCCESS)
    {
        len = OS_strnlen(sock->stream_name, sizeof(sock->stream_name));
        snprintf(&sock->stream_name[len], sizeof(sock->stream_name) - len, ":%u", (unsigned int)port);
    }

    if (parent_name)
    {
        /* Append the name from the parent socket. */
        len = OS_strnlen(sock->stream_name, sizeof(sock->stream_name));
        snprintf(&sock->stream_name[len], sizeof(sock->stream_name) - len, "-%s", parent_name);
    }
}
int32 OS_SocketAddrGetPort_Impl(uint16 *PortNum, const OS_SockAddr_t *Addr)
{
    in_port_t                     sa_port;
    const OS_SockAddr_Accessor_t *Accessor;

    Accessor = (const OS_SockAddr_Accessor_t *)&Addr->AddrData;

    switch (Accessor->sa.sa_family)
    {
        case AF_INET:
            sa_port = Accessor->sa_in.sin_port;
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case AF_INET6:
            sa_port = Accessor->sa_in6.sin6_port;
            break;
#endif
        default:
            return OS_ERR_BAD_ADDRESS;
            break;
    }

    *PortNum = ntohs(sa_port);

    return OS_SUCCESS;
}
void *UT_Stub_GetReturnValuePtr(UT_EntryKey_t FuncKey, size_t ReturnSize, const char *TypeName)
{
    UT_StubTableEntry_t *StubPtr;
    void *               ReturnPtr;

    ReturnPtr = NULL;
    StubPtr   = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_RETURN_BUFFER);

    if (StubPtr == NULL)
    {
        /* This shouldn't happen, it means the stub tried to use a
         * return buffer that does not exist.
         *
         * It is most likely caused by a mismatch/incompatibility between
         * stub and handler.  Aborting now is better than segfaulting later,
         * as the errored call should still be on the stack trace
         */
        UtAssert_Abort("Return buffer invalid");
    }
    else
    {
        ReturnPtr = StubPtr->Data.Buff.BasePtr;

        /*
         * Sanity check on the size and type name.
         *
         * In a generated stub these should always match, but if any patches
         * were done or if a hand-written stub is used, mistakes could be made.
         */
        if (StubPtr->Data.Buff.Position != ReturnSize || strcmp(TypeName, StubPtr->Data.Buff.ContentTypeStr) != 0)
        {
            UtAssert_Failed("Return value mismatch, expected %s(%lu) got %s(%lu)", TypeName, (unsigned long)ReturnSize,
                            StubPtr->Data.Buff.ContentTypeStr, (unsigned long)StubPtr->Data.Buff.Position);
        }
    }

    return ReturnPtr;
}
