#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <errno.h>
#include <time.h>
#include <sys/types.h>
#include <sys/socket.h>

/* Helper probe function: appends a formatted log line to /tmp/logs/1093.txt */
static void OS_SocketConnect_Log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/1093.txt", "a");
    if (f == NULL)
    {
        return;
    }

    /* timestamp */
    time_t t = time(NULL);
    struct tm tm_buf;
    if (localtime_r(&t, &tm_buf) != NULL)
    {
        char timestr[64];
        if (strftime(timestr, sizeof(timestr), "%Y-%m-%d %H:%M:%S", &tm_buf) > 0)
        {
            fprintf(f, "%s - ", timestr);
        }
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

int32 OS_SocketConnect_Impl(const OS_object_token_t *token, const OS_SockAddr_t *Addr, int32 timeout)
{
    int32                           return_code;
    int                             os_status;
    int                             sockopt;
    socklen_t                       slen;
    uint32                          operation;
    const struct sockaddr *         sa;
    OS_impl_file_internal_record_t *impl;

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    /* Log basic entry state */
    OS_SocketConnect_Log("Entered OS_SocketConnect_Impl: token=%p, impl=%p, Addr=%p, timeout=%d",
                          (const void *)token, (const void *)impl, (const void *)Addr, (int)timeout);

    if (impl != NULL)
    {
        /* Probe impl fields that are used as read-only in this function */
        OS_SocketConnect_Log("impl->fd=%d, impl->selectable=%d", (int)impl->fd, (int)impl->selectable);
    }
    else
    {
        OS_SocketConnect_Log("impl is NULL");
    }

    sa = (const struct sockaddr *)&Addr->AddrData;
    OS_SocketConnect_Log("sa->sa_family=%d", (int)sa->sa_family);

    switch (sa->sa_family)
    {
        case AF_INET:
            slen = sizeof(struct sockaddr_in);
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case AF_INET6:
            slen = sizeof(struct sockaddr_in6);
            break;
#endif
        default:
            slen = 0;
            break;
    }

    OS_SocketConnect_Log("computed slen=%u, Addr->ActualLength=%u", (unsigned)slen, (unsigned)Addr->ActualLength);

    if (slen != Addr->ActualLength)
    {
        OS_SocketConnect_Log("Address length mismatch, returning OS_ERR_BAD_ADDRESS");
        return_code = OS_ERR_BAD_ADDRESS;
    }
    else
    {
        return_code = OS_SUCCESS;
        OS_SocketConnect_Log("Calling connect(fd=%d)", (int)impl->fd);
        os_status   = connect(impl->fd, sa, slen);
        OS_SocketConnect_Log("connect returned os_status=%d, errno=%d (%s)", os_status, errno, strerror(errno));
        if (os_status < 0)
        {
            if (errno != EINPROGRESS)
            {
                OS_DEBUG("connect: %s\n", strerror(errno));
                OS_SocketConnect_Log("connect failed with errno %d (not EINPROGRESS), returning OS_ERROR", errno);
                return_code = OS_ERROR;
            }
            else
            {
                OS_SocketConnect_Log("connect returned EINPROGRESS, entering non-blocking wait path");
                /*
                 * If the socket was created in nonblocking mode (O_NONBLOCK flag) then the connect
                 * runs in the background and connect() returns EINPROGRESS.  In this case we still
                 * want to provide the "normal" (blocking) semantics to the calling app, such that
                 * when OS_SocketConnect() returns, the socket is ready for use.
                 *
                 * To provide consistent behavior to calling apps, this does a select() to wait
                 * for the socket to become writable, meaning that the remote side is connected.
                 *
                 * An important point here is that the calling app can control the timeout.  If the
                 * normal/blocking connect() was used, the OS/IP stack controls the timeout, and it
                 * can be quite long.
                 */
                operation = OS_STREAM_STATE_WRITABLE;
                if (impl->selectable)
                {
                    OS_SocketConnect_Log("impl is selectable, calling OS_SelectSingle_Impl with timeout=%d", (int)timeout);
                    return_code = OS_SelectSingle_Impl(token, &operation, timeout);
                    OS_SocketConnect_Log("OS_SelectSingle_Impl returned %d, operation=0x%08x", (int)return_code, (unsigned)operation);
                }
                else
                {
                    OS_SocketConnect_Log("impl not selectable, skipping OS_SelectSingle_Impl");
                }
                if (return_code == OS_SUCCESS)
                {
                    if ((operation & OS_STREAM_STATE_WRITABLE) == 0)
                    {
                        OS_SocketConnect_Log("select returned but socket not writable, returning OS_ERROR_TIMEOUT");
                        return_code = OS_ERROR_TIMEOUT;
                    }
                    else
                    {
                        /*
                         * The SO_ERROR socket flag should also read back zero.
                         * If not zero, something went wrong during connect
                         */
                        sockopt   = 0;
                        slen      = sizeof(sockopt);
                        OS_SocketConnect_Log("calling getsockopt(fd=%d) to read SO_ERROR", (int)impl->fd);
                        os_status = getsockopt(impl->fd, SOL_SOCKET, SO_ERROR, &sockopt, &slen);
                        OS_SocketConnect_Log("getsockopt returned os_status=%d, sockopt=%d", os_status, sockopt);
                        if (os_status < 0 || sockopt != 0)
                        {
                            OS_SocketConnect_Log("getsockopt indicates error, returning OS_ERROR");
                            return_code = OS_ERROR;
                        }
                    }
                }
            }
        }
    }

    OS_SocketConnect_Log("Exiting OS_SocketConnect_Impl with return_code=%d", (int)return_code);
    return return_code;
}
