/* Probe logging helper - appends human-readable logs to /tmp/logs/2683.txt */
static void OS_TimeBaseGetInfo_Log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/2683.txt", "a");
    if (f == NULL)
    {
        return;
    }

    time_t now = time(NULL);
    if (now != (time_t)-1)
    {
        fprintf(f, "[%ld] ", (long)now);
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

