#include <stdio.h>
#include <stdint.h>
#include <inttypes.h>

/* Probe logging helper: appends structured logs to /tmp/logs/2699.txt */
static void OS_TimeBaseSet_Log(const char *stage, osal_id_t timer_id, uint32 start_time, uint32 interval_time, osal_objtype_t objtype, int32 return_code, OS_timebase_internal_record_t *timebase)
{
    FILE *f = fopen("/tmp/logs/2699.txt", "a");
    if (f == NULL)
    {
        return;
    }

    /* Print basic parameters and control-stage */
    fprintf(f, "[OS_TimeBaseSet] stage=%s timer_id=0x%" PRIxPTR " start_time=%u interval_time=%u objtype=%d return_code=%d timebase=%p\n",
            stage, (uintptr_t)timer_id, (unsigned)start_time, (unsigned)interval_time, (int)objtype, (int)return_code, (void *)timebase);

    if (timebase != NULL)
    {
        /* Print existing nominal values to capture pre/post state */
        fprintf(f, "  timebase->nominal_start_time=%u nominal_interval_time=%u\n",
                (unsigned)timebase->nominal_start_time, (unsigned)timebase->nominal_interval_time);
    }

    fclose(f);
}

int32 OS_TimeBaseSet(osal_id_t timer_id, uint32 start_time, uint32 interval_time)
{
    int32                          return_code;
    osal_objtype_t                 objtype;
    OS_object_token_t              token;
    OS_timebase_internal_record_t *timebase;

    /* Log entry with provided parameters */
    OS_TimeBaseSet_Log("entry", timer_id, start_time, interval_time, (osal_objtype_t)-1, -1, NULL);

    /*
     * Internally the implementation represents the interval as a
     * signed 32-bit integer, but the parameter is unsigned because a negative interval
     * does not make sense.
     *
     * Note that the units are intentionally left unspecified.  The external sync period
     * could be measured in microseconds or hours -- it is whatever the application requires.
     */
    ARGCHECK(start_time < 1000000000, OS_TIMER_ERR_INVALID_ARGS);
    ARGCHECK(interval_time < 1000000000, OS_TIMER_ERR_INVALID_ARGS);

    /*
     * Check our context.  Not allowed to use the timer API from a timer callback.
     * Just interested in the object type returned.
     */
    objtype = OS_ObjectIdToType_Impl(OS_TaskGetId_Impl());

    /* Log result of context check */
    OS_TimeBaseSet_Log("after_context_check", timer_id, start_time, interval_time, objtype, -1, NULL);

    if (objtype == OS_OBJECT_TYPE_OS_TIMEBASE)
    {
        OS_TimeBaseSet_Log("incorrect_obj_state_return", timer_id, start_time, interval_time, objtype, OS_ERR_INCORRECT_OBJ_STATE, NULL);
        return OS_ERR_INCORRECT_OBJ_STATE;
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_OBJECT_TYPE_OS_TIMEBASE, timer_id, &token);

    /* Log result of object lookup */
    OS_TimeBaseSet_Log("after_object_get", timer_id, start_time, interval_time, objtype, return_code, NULL);

    if (return_code == OS_SUCCESS)
    {
        timebase = OS_OBJECT_TABLE_GET(OS_timebase_table, token);

        /* Log pointer to timebase and pre-lock state */
        OS_TimeBaseSet_Log("before_lock", timer_id, start_time, interval_time, objtype, return_code, timebase);

        /* Need to take the time base lock to ensure that no ticks are currently being processed */
        OS_TimeBaseLock_Impl(&token);

        /* Log that we have acquired the lock */
        OS_TimeBaseSet_Log("locked", timer_id, start_time, interval_time, objtype, return_code, timebase);

        return_code = OS_TimeBaseSet_Impl(&token, start_time, interval_time);

        /* Log result of implementation call and intermediate state */
        OS_TimeBaseSet_Log("after_impl_set", timer_id, start_time, interval_time, objtype, return_code, timebase);

        if (return_code == OS_SUCCESS)
        {
            /* Save the value since we were successful */
            timebase->nominal_start_time    = start_time;
            timebase->nominal_interval_time = interval_time;

            /* Log post-update state */
            OS_TimeBaseSet_Log("updated_nominal_values", timer_id, start_time, interval_time, objtype, return_code, timebase);
        }

        OS_TimeBaseUnlock_Impl(&token);

        /* Log after unlock and before release */
        OS_TimeBaseSet_Log("unlocked", timer_id, start_time, interval_time, objtype, return_code, timebase);

        OS_ObjectIdRelease(&token);

        /* Final log before returning */
        OS_TimeBaseSet_Log("release_and_exit", timer_id, start_time, interval_time, objtype, return_code, timebase);
    }

    return return_code;
}
