#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <string.h>

/* Probe logging helper: append formatted messages to /tmp/logs/2753.txt */
static void OS_TimerGetInfo_Log(const char *fmt, ...)
{
    va_list args;
    FILE *fp = fopen("/tmp/logs/2753.txt", "a");
    if (fp == NULL)
    {
        return;
    }

    va_start(args, fmt);
    vfprintf(fp, fmt, args);
    fprintf(fp, "\n");
    va_end(args);

    fclose(fp);
}

int32 OS_TimerGetInfo(osal_id_t timer_id, OS_timer_prop_t *timer_prop)
{
    OS_common_record_t *           record;
    int32                          return_code;
    osal_objtype_t                 objtype;
    OS_object_token_t              token;
    OS_timecb_internal_record_t *  timecb;
    OS_timebase_internal_record_t *timebase;

    /* Log function entry and input parameter */
    OS_TimerGetInfo_Log("ENTER: OS_TimerGetInfo(timer_id=%lld, timer_prop=%p)", (long long)timer_id, (void *)timer_prop);

    /* Check parameters */
    OS_CHECK_POINTER(timer_prop);
    OS_TimerGetInfo_Log("PARAM_CHECK: timer_prop pointer checked and non-NULL");

    /*
     * Check our context.  Not allowed to use the timer API from a timer callback.
     * Just interested in the object type returned.
     */
    objtype = OS_ObjectIdToType_Impl(OS_TaskGetId_Impl());
    OS_TimerGetInfo_Log("OBJTYPE_CHECK: current objtype = %d", (int)objtype);
    if (objtype == OS_OBJECT_TYPE_OS_TIMEBASE)
    {
        OS_TimerGetInfo_Log("RETURN_EARLY: called from timebase callback context; returning OS_ERR_INCORRECT_OBJ_STATE");
        return OS_ERR_INCORRECT_OBJ_STATE;
    }

    memset(timer_prop, 0, sizeof(OS_timer_prop_t));
    OS_TimerGetInfo_Log("CLEARED_TIMER_PROP: zeroed timer_prop at %p", (void *)timer_prop);

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_OBJECT_TYPE_OS_TIMECB, timer_id, &token);
    OS_TimerGetInfo_Log("OBJECTID_GET: timer_id=%lld -> return_code=%d, token=%u", (long long)timer_id, (int)return_code, (unsigned)token);
    if (return_code == OS_SUCCESS)
    {
        record   = OS_OBJECT_TABLE_GET(OS_global_timecb_table, token);
        timecb   = OS_OBJECT_TABLE_GET(OS_timecb_table, token);
        /* Log addresses before dereferencing */
        OS_TimerGetInfo_Log("LOOKUP: record=%p, timecb=%p", (void *)record, (void *)timecb);

        if (timecb != NULL)
        {
            timebase = OS_OBJECT_TABLE_GET(OS_timebase_table, timecb->timebase_token);
            OS_TimerGetInfo_Log("TIMECB_STATE: timebase_token=%u, interval_time=%lld", (unsigned)timecb->timebase_token, (long long)timecb->interval_time);
        }
        else
        {
            timebase = NULL;
            OS_TimerGetInfo_Log("TIMECB_NULL: timecb is NULL for token=%u", (unsigned)token);
        }

        if (record != NULL)
        {
            /* Safely log name (ensure null-termination) */
            char name_buf[sizeof(timer_prop->name)];
            strncpy(name_buf, record->name_entry, sizeof(name_buf) - 1);
            name_buf[sizeof(name_buf) - 1] = '\0';
            OS_TimerGetInfo_Log("RECORD_STATE: name_entry='%s', creator=%u", name_buf, (unsigned)record->creator);
        }

        if (timebase != NULL)
        {
            OS_TimerGetInfo_Log("TIMEBASE_STATE: timebase=%p, accuracy_usec=%u", (void *)timebase, (unsigned)timebase->accuracy_usec);
        }
        else
        {
            OS_TimerGetInfo_Log("TIMEBASE_NULL: could not resolve timebase for timecb token");
        }

        /* Preserve original logic: copy out properties */
        strncpy(timer_prop->name, record->name_entry, sizeof(timer_prop->name) - 1);
        timer_prop->creator       = record->creator;
        timer_prop->interval_time = (uint32)timecb->interval_time;
        timer_prop->accuracy      = timebase->accuracy_usec;

        OS_TimerGetInfo_Log("OUTPUT_VALUES: timer_prop->name='%s', creator=%u, interval_time=%u, accuracy=%u",
                            timer_prop->name, (unsigned)timer_prop->creator, (unsigned)timer_prop->interval_time,
                            (unsigned)timer_prop->accuracy);

        OS_ObjectIdRelease(&token);
        OS_TimerGetInfo_Log("RELEASE: token released");
    }
    else
    {
        OS_TimerGetInfo_Log("OBJECTID_GET_FAILED: return_code=%d", (int)return_code);
    }

    OS_TimerGetInfo_Log("EXIT: OS_TimerGetInfo returning %d", (int)return_code);
    return return_code;
}
