#include <stdio.h>
#include <stdarg.h>
#include <string.h>

static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/1782.txt", "a");
    if (f == NULL)
        return;

    va_list args;
    va_start(args, fmt);
    vfprintf(f, fmt, args);
    fprintf(f, "\n");
    va_end(args);
    fclose(f);
}

int32 OS_TranslatePath(const char *VirtualPath, char *LocalPath)
{
    OS_object_token_t             token;
    int32                         return_code;
    const char *                  name_ptr;
    OS_filesys_internal_record_t *filesys;
    size_t                        SysMountPointLen;
    size_t                        VirtPathLen;
    size_t                        VirtPathBegin;

    /*
    ** Check to see if the path pointers are NULL
    */
    /* Check parameters */
    OS_CHECK_POINTER(VirtualPath);
    OS_CHECK_POINTER(LocalPath);

    probe_log("OS_TranslatePath ENTRY: VirtualPath=%p LocalPath=%p", (void*)VirtualPath, (void*)LocalPath);

    /*
    ** Check length
    */
    VirtPathLen = OS_strnlen(VirtualPath, OS_MAX_PATH_LEN);
    probe_log("After strnlen: VirtPathLen=%zu", VirtPathLen);
    if (VirtPathLen >= OS_MAX_PATH_LEN)
    {
        probe_log("Path too long: returning OS_FS_ERR_PATH_TOO_LONG");
        return OS_FS_ERR_PATH_TOO_LONG;
    }

    /* checks to see if there is a '/' somewhere in the path */
    name_ptr = strrchr(VirtualPath, '/');
    probe_log("After strrchr: name_ptr=%p", (void*)name_ptr);
    if (name_ptr == NULL)
    {
        probe_log("No '/' found: returning OS_FS_ERR_PATH_INVALID");
        return OS_FS_ERR_PATH_INVALID;
    }

    /* strrchr returns a pointer to the last '/' char, so we advance one char */
    name_ptr = name_ptr + 1;
    probe_log("name_ptr advanced: name_ptr=%p", (void*)name_ptr);
    if (memchr(name_ptr, 0, OS_MAX_FILE_NAME) == NULL)
    {
        probe_log("File name too long: returning OS_FS_ERR_NAME_TOO_LONG");
        return OS_FS_ERR_NAME_TOO_LONG;
    }

    SysMountPointLen = 0;
    VirtPathBegin    = VirtPathLen;
    probe_log("Initial assignment: SysMountPointLen=%zu VirtPathBegin(initial)=%zu", SysMountPointLen, VirtPathBegin);

    /*
    ** All valid Virtual paths must start with a '/' character
    */
    if (VirtualPath[0] != '/')
    {
        probe_log("VirtualPath does not start with '/': returning OS_FS_ERR_PATH_INVALID");
        return OS_FS_ERR_PATH_INVALID;
    }

    /* Get a reference lock, as a filesystem check could take some time. */
    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)VirtualPath, &token);

    probe_log("After OS_ObjectIdGetBySearch: return_code=%d", return_code);

    if (return_code != OS_SUCCESS)
    {
        probe_log("ObjectIdGetBySearch failed: mapping invalid -> return_code set to OS_FS_ERR_PATH_INVALID");
        return_code = OS_FS_ERR_PATH_INVALID;
    }
    else
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);
        probe_log("Found filesys record: filesys=%p flags=0x%08x", (void*)filesys, (unsigned)filesys->flags);

        if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM) != 0)
        {
            SysMountPointLen = OS_strnlen(filesys->system_mountpt, sizeof(filesys->system_mountpt));
            probe_log("Computed SysMountPointLen=%zu (system_mountpt first bytes='%.200s')", SysMountPointLen, filesys->system_mountpt);

            VirtPathBegin    = OS_strnlen(filesys->virtual_mountpt, sizeof(filesys->virtual_mountpt));
            probe_log("Overwrote VirtPathBegin with virtual mount len=%zu (virtual_mountpt first bytes='%.200s')", VirtPathBegin, filesys->virtual_mountpt);

            if (SysMountPointLen < OS_MAX_LOCAL_PATH_LEN)
            {
                memcpy(LocalPath, filesys->system_mountpt, SysMountPointLen);
                probe_log("Copied system_mountpt to LocalPath (len=%zu)", SysMountPointLen);
            }
        }
        else
        {
            probe_log("Filesystem not a mounted system: setting return_code=OS_ERR_INCORRECT_OBJ_STATE");
            return_code = OS_ERR_INCORRECT_OBJ_STATE;
        }

        OS_ObjectIdRelease(&token);
        probe_log("Released object token");
    }

    probe_log("Before final success-check: return_code=%d VirtPathLen=%zu VirtPathBegin=%zu SysMountPointLen=%zu", return_code, VirtPathLen, VirtPathBegin, SysMountPointLen);

    if (return_code == OS_SUCCESS)
    {
        if (VirtPathLen < VirtPathBegin)
        {
            probe_log("VirtPathLen < VirtPathBegin: %zu < %zu -> OS_FS_ERR_PATH_INVALID", VirtPathLen, VirtPathBegin);
            return_code = OS_FS_ERR_PATH_INVALID;
        }
        else
        {
            VirtPathLen -= VirtPathBegin;
            probe_log("Adjusted VirtPathLen for copying: VirtPathLen(after subtract)=%zu", VirtPathLen);
            if ((SysMountPointLen + VirtPathLen) < OS_MAX_LOCAL_PATH_LEN)
            {
                memcpy(&LocalPath[SysMountPointLen], &VirtualPath[VirtPathBegin], VirtPathLen);
                LocalPath[SysMountPointLen + VirtPathLen] = 0;
                probe_log("Constructed LocalPath: first bytes='%.200s' (SysMountPointLen=%zu VirtPathLen=%zu)", LocalPath, SysMountPointLen, VirtPathLen);
            }
            else
            {
                probe_log("Local path too long: SysMountPointLen(%zu) + VirtPathLen(%zu) >= OS_MAX_LOCAL_PATH_LEN -> OS_FS_ERR_PATH_TOO_LONG", SysMountPointLen, VirtPathLen);
                return_code = OS_FS_ERR_PATH_TOO_LONG;
            }
        }
    }

    probe_log("EXIT: return_code=%d", return_code);

    return return_code;
}
