#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stddef.h>

/* Probe logging helper: appends human-readable traces to /tmp/logs/3382.txt */
static void UT_BSP_LogProbe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/3382.txt", "a");
    if (f == NULL)
    {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

void UT_BSP_DoText(uint8 MessageType, const char *OutputMessage)
{
    char   Buffer[16];
    size_t MsgLen;
    uint32 TermModeBits = OS_BSP_CONSOLEMODE_NORMAL;
    uint32 MsgEnabled   = BSP_UT_Global.CurrVerbosity >> MessageType;

    UT_BSP_LogProbe("UT_BSP_DoText entry: MessageType=%u OutputMessage_ptr=%p MsgEnabled(before &1)=%u TermModeBits(init)=%u",
                    (unsigned)MessageType, (void *)OutputMessage, (unsigned)MsgEnabled, (unsigned)TermModeBits);

    if (MsgEnabled & 1)
    {
        UT_BSP_Lock();
        UT_BSP_LogProbe("Locked for MessageType=%u", (unsigned)MessageType);

        /* Determine if the message type warrants special treatment (color/highlight/etc). */
        UT_BSP_LogProbe("Switch dispatch: entering switch for MessageType=%u", (unsigned)MessageType);
        switch (MessageType)
        {
            case UTASSERT_CASETYPE_ABORT:
            case UTASSERT_CASETYPE_FAILURE:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED;
                UT_BSP_LogProbe("Switch case ABORT/FAILURE taken: TermModeBits=%u", (unsigned)TermModeBits);
                break;
            case UTASSERT_CASETYPE_MIR:
            case UTASSERT_CASETYPE_WARN:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED | OS_BSP_CONSOLEMODE_GREEN;
                UT_BSP_LogProbe("Switch case MIR/WARN taken: TermModeBits=%u", (unsigned)TermModeBits);
                break;
            case UTASSERT_CASETYPE_TSF:
            case UTASSERT_CASETYPE_TTF:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED | OS_BSP_CONSOLEMODE_BLUE;
                UT_BSP_LogProbe("Switch case TSF/TTF taken: TermModeBits=%u", (unsigned)TermModeBits);
                break;
            case UTASSERT_CASETYPE_BEGIN:
                UT_BSP_LogProbe("Switch case BEGIN taken: emitting extra newline");
                OS_BSP_ConsoleOutput_Impl("\n", 1); /* add a bit of extra whitespace between tests */
                break;
            case UTASSERT_CASETYPE_PASS:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_GREEN;
                UT_BSP_LogProbe("Switch case PASS taken: TermModeBits=%u", (unsigned)TermModeBits);
                break;
            default:
                /* Probe: record that the default path was taken. This is the scenario under test:
                 * the default contains only a break (no-op). Logging here provides evidence that control
                 * reached the default and that removing the break would not change observable behavior. */
                UT_BSP_LogProbe("Switch default taken for MessageType=%u: executing 'break' (no-op)", (unsigned)MessageType);
                break;
        }

        if (MsgEnabled <= 1)
        {
            UT_BSP_LogProbe("MsgEnabled <= 1 (MsgEnabled=%u) -> forcing TermModeBits to NORMAL", (unsigned)MsgEnabled);
            TermModeBits = OS_BSP_CONSOLEMODE_NORMAL;
        }

        snprintf(Buffer, sizeof(Buffer), "[%5s]", UtAssert_GetCaseTypeAbbrev(MessageType));
        UT_BSP_LogProbe("Prepared prefix Buffer=\"%s\" TermModeBits=%u", Buffer, (unsigned)TermModeBits);

        if (TermModeBits != OS_BSP_CONSOLEMODE_NORMAL)
        {
            UT_BSP_LogProbe("Setting console mode to %u before printing prefix", (unsigned)TermModeBits);
            OS_BSP_ConsoleSetMode_Impl(TermModeBits);
        }

        OS_BSP_ConsoleOutput_Impl(Buffer, strlen(Buffer));

        if (TermModeBits != OS_BSP_CONSOLEMODE_NORMAL)
        {
            UT_BSP_LogProbe("Restoring console mode to NORMAL after printing prefix");
            OS_BSP_ConsoleSetMode_Impl(OS_BSP_CONSOLEMODE_NORMAL);
        }

        OS_BSP_ConsoleOutput_Impl(" ", 1);
        MsgLen = strlen(OutputMessage);
        UT_BSP_LogProbe("OutputMessage length=%zu OutputMessage_ptr=%p", MsgLen, (void *)OutputMessage);
        OS_BSP_ConsoleOutput_Impl(OutputMessage, MsgLen);
        if (MsgLen == 0 || OutputMessage[MsgLen - 1] != '\n')
        {
            UT_BSP_LogProbe("OutputMessage not terminated with newline; emitting one");
            OS_BSP_ConsoleOutput_Impl("\n", 1);
        }

        UT_BSP_Unlock();
        UT_BSP_LogProbe("Unlocked for MessageType=%u", (unsigned)MessageType);
    }

    /*
     * If any ABORT (major failure) message is thrown,
     * then call a BSP-provided routine to stop the test and possibly dump a core
     */
    if (MessageType == UTASSERT_CASETYPE_ABORT)
    {
        UT_BSP_LogProbe("MessageType is ABORT (%u): calling OS_BSP_Shutdown_Impl()", (unsigned)MessageType);
        OS_BSP_Shutdown_Impl();
    }

    UT_BSP_LogProbe("UT_BSP_DoText exit: MessageType=%u", (unsigned)MessageType);
}
