#include <stdio.h>
#include <stdarg.h>
#include <sys/stat.h>
#include <errno.h>
#include <string.h>
#include <stddef.h>
#include <stdint.h>

/*
 * Probe/logging helpers - write human readable traces to /tmp/logs/3389.txt
 * These helpers are intentionally minimal and do not change program flow.
 */
static void UT_EnsureLogDir(void)
{
    /* Try to create the directory; ignore errors if it already exists */
    (void)mkdir("/tmp/logs", 0777);
}

static void UT_LogProbe(const char *fmt, ...)
{
    UT_EnsureLogDir();
    FILE *f = fopen("/tmp/logs/3389.txt", "a");
    if (!f)
    {
        return; /* best effort logging only */
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fputc('\n', f);
    fclose(f);
}

void UT_BSP_DoText(uint8 MessageType, const char *OutputMessage)
{
    char   Buffer[16];
    size_t MsgLen;
    uint32 TermModeBits = OS_BSP_CONSOLEMODE_NORMAL;
    uint32 MsgEnabled   = BSP_UT_Global.CurrVerbosity >> MessageType;

    /* Log entry and initial state */
    UT_LogProbe("UT_BSP_DoText ENTER: MessageType=%u, OutputMessage=%p, CurrVerbosity=%u, MsgEnabled(before mask)=%u",
                (unsigned)MessageType, (const void *)OutputMessage, (unsigned)BSP_UT_Global.CurrVerbosity, (unsigned)MsgEnabled);

    if (MsgEnabled & 1)
    {
        UT_BSP_Lock();

        /* Determine if the message type warrants special treatment (color/highlight/etc). */
        switch (MessageType)
        {
            case UTASSERT_CASETYPE_ABORT:
            case UTASSERT_CASETYPE_FAILURE:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED;
                break;
            case UTASSERT_CASETYPE_MIR:
            case UTASSERT_CASETYPE_WARN:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED | OS_BSP_CONSOLEMODE_GREEN;
                break;
            case UTASSERT_CASETYPE_TSF:
            case UTASSERT_CASETYPE_TTF:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED | OS_BSP_CONSOLEMODE_BLUE;
                break;
            case UTASSERT_CASETYPE_BEGIN:
                OS_BSP_ConsoleOutput_Impl("\n", 1); /* add a bit of extra whitespace between tests */
                break;
            case UTASSERT_CASETYPE_PASS:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_GREEN;
                break;
            default:
                break;
        }

        if (MsgEnabled <= 1)
        {
            TermModeBits = OS_BSP_CONSOLEMODE_NORMAL;
        }

        snprintf(Buffer, sizeof(Buffer), "[%5s]", UtAssert_GetCaseTypeAbbrev(MessageType));

        /* Log the buffer that will be output */
        UT_LogProbe("Prepared Buffer=%s (addr=%p)", Buffer, (void *)Buffer);

        if (TermModeBits != OS_BSP_CONSOLEMODE_NORMAL)
        {
            OS_BSP_ConsoleSetMode_Impl(TermModeBits);
            UT_LogProbe("Console mode set to bits=0x%08x", (unsigned)TermModeBits);
        }

        /* Log before calling strlen(Buffer) to capture control flow */
        UT_LogProbe("About to output Buffer via OS_BSP_ConsoleOutput_Impl; strlen(Buffer)=%zu", strlen(Buffer));
        OS_BSP_ConsoleOutput_Impl(Buffer, strlen(Buffer));

        if (TermModeBits != OS_BSP_CONSOLEMODE_NORMAL)
        {
            OS_BSP_ConsoleSetMode_Impl(OS_BSP_CONSOLEMODE_NORMAL);
            UT_LogProbe("Console mode restored to NORMAL");
        }

        OS_BSP_ConsoleOutput_Impl(" ", 1);

        /*
         * Probe and log the pointer and state immediately before calling strlen(OutputMessage).
         * This is intentionally done exactly where the original code calls strlen so that
         * any crash/undefined behavior can be reproduced while still capturing the pointer
         * and relevant state just beforehand.
         */
        UT_LogProbe("About to compute MsgLen = strlen(OutputMessage); OutputMessage=%p", (const void *)OutputMessage);
        if (OutputMessage == NULL)
        {
            UT_LogProbe("WARNING: OutputMessage is NULL. The subsequent strlen(OutputMessage) call will likely crash.");
        }

        MsgLen = strlen(OutputMessage);
        UT_LogProbe("Computed MsgLen=%zu for OutputMessage=%p", MsgLen, (const void *)OutputMessage);

        OS_BSP_ConsoleOutput_Impl(OutputMessage, MsgLen);
        if (MsgLen == 0 || OutputMessage[MsgLen - 1] != '\n')
        {
            OS_BSP_ConsoleOutput_Impl("\n", 1);
        }

        UT_BSP_Unlock();

        UT_LogProbe("UT_BSP_DoText: finished output for MessageType=%u", (unsigned)MessageType);
    }

    /*
     * If any ABORT (major failure) message is thrown,
     * then call a BSP-provided routine to stop the test and possibly dump a core
     */
    if (MessageType == UTASSERT_CASETYPE_ABORT)
    {
        UT_LogProbe("MessageType is ABORT; calling OS_BSP_Shutdown_Impl()");
        OS_BSP_Shutdown_Impl();
    }

    UT_LogProbe("UT_BSP_DoText EXIT: MessageType=%u", (unsigned)MessageType);
}
