#include <stdio.h>
#include <stdint.h>

uint32 UT_GetStubCount(UT_EntryKey_t FuncKey)
{
    UT_StubTableEntry_t *StubPtr;
    uint32               Count;
    FILE *fp = fopen("/tmp/logs/3063.txt", "a");

    if (fp != NULL)
    {
        /* Log entry and input key to help reproduce the scenario */
        fprintf(fp, "UT_GetStubCount called: FuncKey=%lu\n", (unsigned long)FuncKey);
    }

    StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_COUNTER);

    if (fp != NULL)
    {
        /* Log the pointer value returned by the lookup */
        fprintf(fp, "UT_GetStubEntry returned StubPtr=%p\n", (void *)StubPtr);
    }

    if (StubPtr != NULL)
    {
        /* Read-only access as in the original code; log the read value. */
        Count = StubPtr->Data.Rc.Count;
        if (fp != NULL)
        {
            fprintf(fp, "Read Count = %u from StubPtr->Data.Rc.Count\n", (unsigned)Count);
        }
    }
    else
    {
        Count = 0;
        if (fp != NULL)
        {
            fprintf(fp, "StubPtr is NULL, Count set to 0\n");
        }
    }

    if (fp != NULL)
    {
        fprintf(fp, "UT_GetStubCount returning Count = %u\n\n", (unsigned)Count);
        fclose(fp);
    }

    return Count;
}
