#include <stdio.h>
#include <string.h>
#include <stdbool.h>
#include <time.h>

/* Probe logging function. Writes control-flow and state info to /tmp/logs/3213.txt */
static void UT_Log_IsValueCompatible(const void *Rvc_ptr, const char *TypeName, const char *WantTypeName, int RvcGenre, int WantGenre, const char *stage, const char *extra)
{
    FILE *f = fopen("/tmp/logs/3213.txt", "a");
    if (f == NULL)
    {
        return;
    }

    time_t t = time(NULL);
    fprintf(f, "[%ld] %s: Rvc=%p, Rvc->TypeName=%p", (long)t, stage, Rvc_ptr, (const void *)TypeName);
    if (TypeName != NULL)
    {
        /* Print the pointed-to string for inspection; do not modify it */
        fprintf(f, ", TypeName=\"%s\"", TypeName);
    }
    else
    {
        fprintf(f, ", TypeName=NULL");
    }

    fprintf(f, ", WantTypeName=%p", (const void *)WantTypeName);
    if (WantTypeName != NULL)
    {
        fprintf(f, ", WantTypeName=\"%s\"", WantTypeName);
    }
    else
    {
        fprintf(f, ", WantTypeName=NULL");
    }

    fprintf(f, ", Rvc->Genre=%d, WantGenre=%d", RvcGenre, WantGenre);
    if (extra != NULL)
    {
        fprintf(f, ", info=%s", extra);
    }
    fprintf(f, "\n");
    fclose(f);
}

/*
 * Original function, augmented with logging probes to capture the control flow
 * and string/pointer state for the Rule 8.13 violation scenario.
 */
bool UT_Stub_IsValueCompatible(const UT_RetvalConfigEntry_t *Rvc, const char *WantTypeName, UT_ValueGenre_t WantGenre)
{
    bool IsCompatible;

    /* Log entry state */
    UT_Log_IsValueCompatible(Rvc, (Rvc != NULL) ? Rvc->TypeName : NULL, WantTypeName,
                             (Rvc != NULL) ? (int)Rvc->Genre : -1, (int)WantGenre,
                             "ENTRY", NULL);

    /* If the type names were specified on both sides, and they match, then the value is compatible */
    if (Rvc->TypeName != NULL && WantTypeName != NULL)
    {
        int cmp = strcmp(Rvc->TypeName, WantTypeName);

        /* Log strcmp input and result */
        char extra[64];
        (void)snprintf(extra, sizeof(extra), "strcmp_result=%d", cmp);
        UT_Log_IsValueCompatible(Rvc, Rvc->TypeName, WantTypeName, (int)Rvc->Genre, (int)WantGenre, "TYPE_NAME_COMPARE", extra);

        IsCompatible = (cmp == 0);

        /* Log branch decision */
        UT_Log_IsValueCompatible(Rvc, Rvc->TypeName, WantTypeName, (int)Rvc->Genre, (int)WantGenre,
                                 "AFTER_COMPARE", IsCompatible ? "match=true" : "match=false");
    }
    else
    {
        /* The types are not _directly_ assignable, but it could possibly be coerced */
        IsCompatible = false;

        /* Log why path taken */
        UT_Log_IsValueCompatible(Rvc, (Rvc != NULL) ? Rvc->TypeName : NULL, WantTypeName,
                                 (Rvc != NULL) ? (int)Rvc->Genre : -1, (int)WantGenre,
                                 "NO_TYPE_NAME", "one_or_both_type_names_null");
    }

    /*
     * As an alternative/fallback, if the value genre is known for both sides,
     * check if coercion is a possibility.  For example, this could apply if the
     * value was int32 on one side and int16 on the other.
     */
    if (!IsCompatible && Rvc->Genre != UT_ValueGenre_OPAQUE && Rvc->Genre == WantGenre)
    {
        /* Log before genre-based compatibility decision */
        UT_Log_IsValueCompatible(Rvc, Rvc->TypeName, WantTypeName, (int)Rvc->Genre, (int)WantGenre,
                                 "GENRE_CHECK", "genre_matches_and_not_opaque");

        /* Genre match - should be compatible, with a potential size adjustment */
        IsCompatible = true;

        /* Log after setting compatible */
        UT_Log_IsValueCompatible(Rvc, Rvc->TypeName, WantTypeName, (int)Rvc->Genre, (int)WantGenre,
                                 "GENRE_DECISION", "set_compatible_true");
    }
    else
    {
        /* If we didn't enter genre compatibility, log why */
        if (!IsCompatible)
        {
            UT_Log_IsValueCompatible(Rvc, Rvc->TypeName, WantTypeName, (int)Rvc->Genre, (int)WantGenre,
                                     "GENRE_DECISION", "not_compatible_after_genre_check");
        }
    }

    /* Final result log */
    UT_Log_IsValueCompatible(Rvc, Rvc->TypeName, WantTypeName, (int)Rvc->Genre, (int)WantGenre,
                             "EXIT", IsCompatible ? "result=true" : "result=false");

    return IsCompatible;
}
