#include <stdio.h>
#include <string.h>
#include <ctype.h>

/* Probe/logging helpers - write trace info to /tmp/logs/2847.txt */
static void UtAssert_LogInitialState(bool IsUnsigned, UT_IntCheck_t ActualValue, UtAssert_Compare_t CompareType,
                                     UT_IntCheck_t RefValue, const char *File, uint32 Line, UtAssert_Radix_t RadixType,
                                     const char *Typename, const char *ActualText, const char *RefText)
{
    FILE *f = fopen("/tmp/logs/2847.txt", "a");
    if (f == NULL)
    {
        return;
    }

    fprintf(f, "ENTRY UtAssert_GenericIntegerCompare\n");
    fprintf(f, "  File: %s\n", (File != NULL) ? File : "(null)");
    fprintf(f, "  Line: %u\n", (unsigned int)Line);
    fprintf(f, "  IsUnsigned: %d\n", (int)IsUnsigned);

    /* Print integer values generically to capture state; cast to long long for safety in logs */
    fprintf(f, "  ActualValue: %lld\n", (long long)ActualValue);
    fprintf(f, "  RefValue   : %lld\n", (long long)RefValue);

    fprintf(f, "  CompareType: %d\n", (int)CompareType);
    fprintf(f, "  RadixType  : %d (initial)\n", (int)RadixType);
    fprintf(f, "  Typename   : %s\n", (Typename != NULL) ? Typename : "(null)");
    fprintf(f, "  ActualText : %s\n", (ActualText != NULL) ? ActualText : "(null)");
    fprintf(f, "  RefText    : %s\n", (RefText != NULL) ? RefText : "(null)");

    fclose(f);
}

static void UtAssert_LogTagState(const char *TagStr, int TagLen)
{
    FILE *f = fopen("/tmp/logs/2847.txt", "a");
    if (f == NULL)
    {
        return;
    }
    fprintf(f, "  TagStr after processing: '%s'\n", (TagStr != NULL) ? TagStr : "(null)");
    fprintf(f, "  TagLen: %d\n", TagLen);
    fclose(f);
}

static void UtAssert_LogRadixChange(UtAssert_Radix_t oldRadix, UtAssert_Radix_t newRadix, const char *Typename)
{
    FILE *f = fopen("/tmp/logs/2847.txt", "a");
    if (f == NULL)
    {
        return;
    }
    fprintf(f, "  Radix decision: Typename='%s' -> strchr(Typename,'*') triggered hex assignment\n", (Typename != NULL) ? Typename : "(null)");
    fprintf(f, "  Radix changed from %d to %d\n", (int)oldRadix, (int)newRadix);
    fclose(f);
}

static void UtAssert_LogFinalState(UtAssert_Radix_t RadixType, const char *TagStr, const char *ActualText, const char *RefText)
{
    FILE *f = fopen("/tmp/logs/2847.txt", "a");
    if (f == NULL)
    {
        return;
    }
    fprintf(f, "EXIT UtAssert_GenericIntegerCompare\n");
    fprintf(f, "  Final RadixType: %d\n", (int)RadixType);
    fprintf(f, "  TagStr: '%s'\n", (TagStr != NULL) ? TagStr : "(null)");
    fprintf(f, "  ActualText (post-strip): %s\n", (ActualText != NULL) ? ActualText : "(null)");
    fprintf(f, "  RefText (post-strip)   : %s\n", (RefText != NULL) ? RefText : "(null)");
    fprintf(f, "----------------------------------------\n");
    fclose(f);
}

bool UtAssert_GenericIntegerCompare(bool IsUnsigned, UT_IntCheck_t ActualValue, UtAssert_Compare_t CompareType,
                                    UT_IntCheck_t RefValue, const char *File, uint32 Line, UtAssert_Radix_t RadixType,
                                    const char *Typename, const char *ActualText, const char *RefText)
{
    static const char UTASSERT_PREFIX[] = "UTASSERT_";

    char ActualStr[32];
    char RefStr[32];
    char TagStr[32];
    int  TagLen;

    /* Log entry state */
    UtAssert_LogInitialState(IsUnsigned, ActualValue, CompareType, RefValue, File, Line, RadixType, Typename, ActualText, RefText);

    /* If the radix type was not specified, then check if the typename appears to be a pointer -
     * That is, it contains an asterisk.  This is far from foolproof due to typedefs etc but
     * it should catch most of them (note that "hiding" a pointer via typedef is discouraged by
     * GSFC coding standards, so this shouldn't be too likely)
     */
    if (Typename != NULL && *Typename != 0)
    {
        TagLen = snprintf(TagStr, sizeof(TagStr), "%s", Typename);
        if (TagLen < 0)
        {
            TagLen = 0;
        }
        else if (TagLen > (sizeof(TagStr) - 3))
        {
            TagLen = sizeof(TagStr) - 3;
        }

        while (TagLen > 0 && (isspace((unsigned char)TagStr[TagLen - 1]) || TagStr[TagLen - 1] == ':'))
        {
            --TagLen;
        }

        if (TagLen > 0)
        {
            TagStr[TagLen] = ':';
            ++TagLen;
            TagStr[TagLen] = ' ';
            ++TagLen;
        }

        TagStr[TagLen] = 0;

        /* Log TagStr state after trimming */
        UtAssert_LogTagState(TagStr, TagLen);

        if (RadixType == UtAssert_Radix_DEFAULT && strchr(Typename, '*') != NULL)
        {
            /* looks like a pointer type */
            /* Log the decision and the radix change for reproducibility/evidence */
            UtAssert_LogRadixChange(RadixType, UtAssert_Radix_HEX, Typename);

            RadixType = UtAssert_Radix_HEX;
        }
    }
    else
    {
        TagStr[0] = 0;
    }

    /* If either the actual text or the ref text starts with the "UTASSERT_" prefix, then strip it */
    if (strncmp(ActualText, UTASSERT_PREFIX, sizeof(UTASSERT_PREFIX) - 1) == 0)
    {
        ActualText += sizeof(UTASSERT_PREFIX) - 1;
    }
    if (strncmp(RefText, UTASSERT_PREFIX, sizeof(UTASSERT_PREFIX) - 1) == 0)
    {
        RefText += sizeof(UTASSERT_PREFIX) - 1;
    }

    /* Log final state just before performing the compare/formatting so the changed RadixType is captured */
    UtAssert_LogFinalState(RadixType, TagStr, ActualText, RefText);

    return UtAssertEx(UtAssert_DoCompare(ActualValue, CompareType, RefValue, IsUnsigned), UTASSERT_CASETYPE_FAILURE,
                      File, Line, "%s%s (%s) %s %s (%s)", TagStr, ActualText,
                      UtAssert_GetValueText(ActualStr, sizeof(ActualStr), ActualValue, IsUnsigned, RadixType),
                      UtAssert_GetOpText(CompareType), RefText,
                      UtAssert_GetValueText(RefStr, sizeof(RefStr), RefValue, IsUnsigned, RadixType));
}
