#include <stdio.h>
#include <sys/stat.h>
#include <errno.h>

/*
 * Probing/logging helpers to record the control path and state for
 * UtAssert_GetCaseTypeAbbrev. Logs are appended to /tmp/logs/2859.txt.
 * These helpers do not alter the original logic and only perform
 * best-effort logging (they do not change return values or control flow).
 */
static void UtAssert_Log(const char *stage, int caseVal, const char *abbrev)
{
    FILE *fp = fopen("/tmp/logs/2859.txt", "a");
    if (fp == NULL)
    {
        /* Attempt to create the parent directory then retry once */
        (void)mkdir("/tmp/logs", 0777);
        fp = fopen("/tmp/logs/2859.txt", "a");
    }

    if (fp != NULL)
    {
        /* Print stage, numeric case value, abbreviation string (if any), and its address */
        fprintf(fp, "%s: CaseType=%d, AbbrevStr=\"%s\", AbbrevAddr=%p\n",
                stage,
                caseVal,
                (abbrev != NULL) ? abbrev : "(null)",
                (const void *)abbrev);
        fclose(fp);
    }
}

const char *UtAssert_GetCaseTypeAbbrev(UtAssert_CaseType_t CaseType)
{
    const char *AbbrevStr;

    /* Log entry with the numeric CaseType; Abbrev not yet determined */
    UtAssert_Log("ENTER", (int)CaseType, NULL);

    switch (CaseType)
    {
        case UTASSERT_CASETYPE_ABORT:
            AbbrevStr = "ABORT";
            break;
        case UTASSERT_CASETYPE_FAILURE:
            AbbrevStr = "FAIL";
            break;
        case UTASSERT_CASETYPE_MIR:
            AbbrevStr = "MIR";
            break;
        case UTASSERT_CASETYPE_TSF:
            AbbrevStr = "TSF";
            break;
        case UTASSERT_CASETYPE_TTF:
            AbbrevStr = "TTF";
            break;
        case UTASSERT_CASETYPE_WARN:
            AbbrevStr = "WARN";
            break;
        case UTASSERT_CASETYPE_NA:
            AbbrevStr = "N/A";
            break;
        case UTASSERT_CASETYPE_BEGIN:
            AbbrevStr = "BEGIN";
            break;
        case UTASSERT_CASETYPE_END:
            AbbrevStr = "END";
            break;
        case UTASSERT_CASETYPE_PASS:
            AbbrevStr = "PASS";
            break;
        case UTASSERT_CASETYPE_INFO:
            AbbrevStr = "INFO";
            break;
        case UTASSERT_CASETYPE_FLOW:
            AbbrevStr = "FLOW";
            break;
        case UTASSERT_CASETYPE_DEBUG:
            AbbrevStr = "DEBUG";
            break;
        default:
            /* do not return NULL, as the result may be directly passed to C library functions */
            AbbrevStr = "OTHER";
            break;
    }

    /* Log the selected abbreviation and return value info before returning */
    UtAssert_Log("EXIT", (int)CaseType, AbbrevStr);

    return AbbrevStr;
}
