#include <stdio.h>

/*
 * Probe function to log pointer adjustments. Other agents (e.g., the one that
 * increments a pointer parameter) can call this to record the before/after
 * pointer values and associated parameter name for evidence collection.
 */
void UtAssert_Probe_PointerAdjustment(const char *param_name, const char *before, const char *after)
{
    FILE *f = fopen("/tmp/logs/2867.txt", "a");
    if (f)
    {
        fprintf(f, "Probe PointerAdjustment: %s before=\"%s\" after=\"%s\"\n",
                param_name ? param_name : "(null)",
                before ? before : "(null)",
                after ? after : "(null)");
        fclose(f);
    }
}

/*
 * Preserve original logic of UtAssert_GetOpText but add logging of the
 * control path (the CompareType received) and the selected OpText value.
 * This produces reproducible evidence of the control flow in
 * /tmp/logs/2867.txt without modifying the original behavior.
 */
const char *UtAssert_GetOpText(UtAssert_Compare_t CompareType)
{
    const char *OpText;

    /* Log function entry and the CompareType value */
    {
        FILE *f = fopen("/tmp/logs/2867.txt", "a");
        if (f)
        {
            fprintf(f, "UtAssert_GetOpText called: CompareType=%d\n", (int)CompareType);
            fclose(f);
        }
    }

    switch (CompareType)
    {
        case UtAssert_Compare_EQ: /* actual equals reference value */
            OpText = "==";
            break;
        case UtAssert_Compare_NEQ: /* actual does not non equal reference value */
            OpText = "!=";
            break;
        case UtAssert_Compare_LT: /* actual less than reference (exclusive) */
            OpText = "<";
            break;
        case UtAssert_Compare_GT: /* actual greater than reference (exclusive)  */
            OpText = ">";
            break;
        case UtAssert_Compare_LTEQ: /* actual less than or equal to reference (inclusive) */
            OpText = "<=";
            break;
        case UtAssert_Compare_GTEQ: /* actual greater than reference (inclusive) */
            OpText = ">=";
            break;
        case UtAssert_Compare_BITMASK_SET: /* bit(s) in reference are set in actual */
            OpText = "&";
            break;
        case UtAssert_Compare_BITMASK_UNSET: /* bit(s) in reference are not set in actual */
            OpText = "&~";
            break;
        default: /* should never happen */
            OpText = "??";
            break;
    }

    /* Log the selected OpText (the control path outcome) */
    {
        FILE *f = fopen("/tmp/logs/2867.txt", "a");
        if (f)
        {
            fprintf(f, "UtAssert_GetOpText selected OpText=\"%s\"\n", OpText ? OpText : "(null)");
            fclose(f);
        }
    }

    return OpText;
}
