#include <stdio.h>
#include <stdarg.h>

/*
 * Probe logging functions to capture control flow and state for debugging.
 * Logs are appended to /tmp/logs/2867.txt.
 */
static void UtAssert_Log(const char *fmt, ...)
{
    FILE *fp = fopen("/tmp/logs/2867.txt", "a");
    if (fp == NULL)
    {
        return; /* best-effort logging; do not alter program logic on failure */
    }

    va_list args;
    va_start(args, fmt);
    vfprintf(fp, fmt, args);
    va_end(args);

    fprintf(fp, "\n");
    fclose(fp);
}

static void UtAssert_LogCompareSelection(int compareType, const char *opText)
{
    /* Log the numeric compare type, the selected text, and the pointer address */
    UtAssert_Log("UtAssert_GetOpText: CompareType=%d, OpText=\"%s\", OpText_ptr=%p", compareType, (opText != NULL) ? opText : "(null)", (const void *)opText);
}

/*
 * Original function logic is preserved; probes added to record the chosen branch
 * and the returned pointer value/address for post-mortem analysis.
 */
const char *UtAssert_GetOpText(UtAssert_Compare_t CompareType)
{
    const char *OpText;

    UtAssert_Log("UtAssert_GetOpText: entry CompareType=%d", (int)CompareType);

    switch (CompareType)
    {
        case UtAssert_Compare_EQ: /* actual equals reference value */
            OpText = "==";
            break;
        case UtAssert_Compare_NEQ: /* actual does not non equal reference value */
            OpText = "!=";
            break;
        case UtAssert_Compare_LT: /* actual less than reference (exclusive) */
            OpText = "<";
            break;
        case UtAssert_Compare_GT: /* actual greater than reference (exclusive)  */
            OpText = ">";
            break;
        case UtAssert_Compare_LTEQ: /* actual less than or equal to reference (inclusive) */
            OpText = "<=";
            break;
        case UtAssert_Compare_GTEQ: /* actual greater than reference (inclusive) */
            OpText = ">=";
            break;
        case UtAssert_Compare_BITMASK_SET: /* bit(s) in reference are set in actual */
            OpText = "&";
            break;
        case UtAssert_Compare_BITMASK_UNSET: /* bit(s) in reference are not set in actual */
            OpText = "&~";
            break;
        default: /* should never happen */
            OpText = "??";
            break;
    }

    /* Record the selection and exit */
    UtAssert_LogCompareSelection((int)CompareType, OpText);
    UtAssert_Log("UtAssert_GetOpText: exit returning OpText_ptr=%p", (const void *)OpText);

    return OpText;
}
