#include <stdio.h>
#include <string.h>
#include <stddef.h>
#include <stdint.h>
#include <stdbool.h>

bool UtAssert_StringBufCompare(const char *String1, size_t String1Max, const char *String2, size_t String2Max,
                               UtAssert_Compare_t CompareType, const char *File, uint32 Line)
{
    char        ScrubbedString1[256];
    char        ScrubbedString2[256];
    const char *EndPtr1;
    const char *EndPtr2;
    size_t      FormatLen1;
    size_t      FormatLen2;
    bool        Result;
    int         Compare;

    /* Open log file for probes (best-effort). */
    FILE *ulog = fopen("/tmp/logs/2899.txt", "a");
    if (ulog)
    {
        fprintf(ulog, "--- UtAssert_StringBufCompare ENTRY ---\n");
        fprintf(ulog, "String1=%p, String1Max=%zu, String2=%p, String2Max=%zu, CompareType=%d, File=%s, Line=%u\n",
                (void *)String1, String1Max, (void *)String2, String2Max, (int)CompareType,
                (File != NULL) ? File : "(null)", (unsigned)Line);
    }

    /* Locate the actual end of both strings */
    if (String1 == NULL)
    {
        if (ulog) fprintf(ulog, "String1 is NULL -> EndPtr1 = NULL\n");
        EndPtr1 = NULL;
    }
    else if (String1Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        if (ulog) fprintf(ulog, "String1Max indicates NUL-terminated; using strlen() for String1\n");
        /*
         * NOTE: it is technically undefined behavior to pass a size to memchr()
         * that is larger than the actual buffer, even if it is known/guaranteed
         * to find a match within the actual buffer.  Therefore the regular strlen()
         * is used instead.
         */
        EndPtr1 = String1 + strlen(String1);
        if (ulog) fprintf(ulog, "EndPtr1=%p (offset %zu)\n", (void *)EndPtr1, (size_t)(EndPtr1 - String1));
    }
    else
    {
        if (ulog) fprintf(ulog, "Calling memchr(String1, 0, %zu)\n", String1Max);
        EndPtr1 = memchr(String1, 0, String1Max);
        if (ulog) fprintf(ulog, "memchr(String1) returned %p\n", (void *)EndPtr1);
    }

    if (EndPtr1 != NULL)
    {
        FormatLen1 = EndPtr1 - String1;
    }
    else
    {
        FormatLen1 = String1Max;
    }
    if (ulog) fprintf(ulog, "FormatLen1=%zu\n", FormatLen1);

    if (String2 == NULL)
    {
        if (ulog) fprintf(ulog, "String2 is NULL -> EndPtr2 = NULL\n");
        EndPtr2 = NULL;
    }
    else if (String2Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        if (ulog) fprintf(ulog, "String2Max indicates NUL-terminated; using strlen() for String2\n");
        EndPtr2 = String2 + strlen(String2);
        if (ulog) fprintf(ulog, "EndPtr2=%p (offset %zu)\n", (void *)EndPtr2, (size_t)(EndPtr2 - String2));
    }
    else
    {
        /* Probe: record that memchr will be invoked on String2 with the provided size */
        if (ulog) fprintf(ulog, "Calling memchr(String2, 0, %zu) -- WARNING: if String2Max > actual buffer, this may read past the buffer\n", String2Max);
        EndPtr2 = memchr(String2, 0, String2Max);
        if (ulog) fprintf(ulog, "memchr(String2) returned %p\n", (void *)EndPtr2);
    }

    if (EndPtr2 != NULL)
    {
        FormatLen2 = EndPtr2 - String2;
    }
    else
    {
        FormatLen2 = String2Max;
    }
    if (ulog) fprintf(ulog, "FormatLen2=%zu\n", FormatLen2);

    if (FormatLen1 == 0 && FormatLen2 == 0)
    {
        /* Two empty strings are considered equal */
        Compare = 0;
        if (ulog) fprintf(ulog, "Both formats length 0 -> Compare = 0\n");
    }
    else
    {
        /* Compare actual content based on the shorter of the two strings */
        if (FormatLen1 < FormatLen2)
        {
            if (ulog) fprintf(ulog, "Calling memcmp(String1, String2, %zu)\n", FormatLen1);
            Compare = memcmp(String1, String2, FormatLen1);
        }
        else
        {
            if (ulog) fprintf(ulog, "Calling memcmp(String1, String2, %zu)\n", FormatLen2);
            Compare = memcmp(String1, String2, FormatLen2);
        }

        if (ulog) fprintf(ulog, "memcmp result = %d\n", Compare);

        /* If initial content was the same, go by whichever is longer */
        if (Compare == 0)
        {
            /*
             * If String1 is longer, compare should be positive (String1 > String2)
             * If String2 is longer, compare should be negative (String1 < String2)
             * If strings are the same length, compare should be 0.
             */
            Compare = (int)(FormatLen1 - FormatLen2);
            if (ulog) fprintf(ulog, "Lengths compared -> Compare = %d (FormatLen1=%zu - FormatLen2=%zu)\n", Compare, FormatLen1, FormatLen2);
        }
    }

    switch (CompareType)
    {
        case UtAssert_Compare_EQ: /* actual equals reference value */
            Result = (Compare == 0);
            break;
        case UtAssert_Compare_NEQ: /* actual does not non equal reference value */
            Result = (Compare != 0);
            break;
        case UtAssert_Compare_LT: /* actual less than reference (exclusive) */
            Result = (Compare < 0);
            break;
        case UtAssert_Compare_GT: /* actual greater than reference (exclusive)  */
            Result = (Compare > 0);
            break;
        case UtAssert_Compare_LTEQ: /* actual less than or equal to reference (inclusive) */
            Result = (Compare <= 0);
            break;
        case UtAssert_Compare_GTEQ: /* actual greater than reference (inclusive) */
            Result = (Compare >= 0);
            break;
        default: /* should never happen */
            Result = false;
            break;
    }

    if (ulog) fprintf(ulog, "Compare=%d, Result=%d -> preparing scrubbed copies\n", Compare, (int)Result);

    /* Now make "safe" copies of the strings */
    /* Check for a newline within the string, and if present, end the string there instead */
    if (FormatLen1 > 0)
    {
        if (ulog) fprintf(ulog, "Checking for newline in String1 within %zu bytes\n", FormatLen1);
        EndPtr1 = memchr(String1, '\n', FormatLen1);
        if (EndPtr1 != NULL)
        {
            FormatLen1 = EndPtr1 - String1;
            if (ulog) fprintf(ulog, "Newline in String1 found at offset %zu -> new FormatLen1=%zu\n", (size_t)(EndPtr1 - String1), FormatLen1);
        }
        memcpy(ScrubbedString1, String1, FormatLen1);
    }
    ScrubbedString1[FormatLen1] = 0;
    if (ulog) fprintf(ulog, "ScrubbedString1 length=%zu\n", FormatLen1);

    if (FormatLen2 > 0)
    {
        if (ulog) fprintf(ulog, "Checking for newline in String2 within %zu bytes\n", FormatLen2);
        EndPtr2 = memchr(String2, '\n', FormatLen2);
        if (EndPtr2 != NULL)
        {
            FormatLen2 = EndPtr2 - String2;
            if (ulog) fprintf(ulog, "Newline in String2 found at offset %zu -> new FormatLen2=%zu\n", (size_t)(EndPtr2 - String2), FormatLen2);
        }
        memcpy(ScrubbedString2, String2, FormatLen2);
    }
    ScrubbedString2[FormatLen2] = 0;
    if (ulog) fprintf(ulog, "ScrubbedString2 length=%zu\n", FormatLen2);

    if (ulog)
    {
        fprintf(ulog, "Final Result=%d -> exiting UtAssert_StringBufCompare\n", (int)Result);
        fprintf(ulog, "-------------------------------------------\n");
        fclose(ulog);
    }

    return UtAssertEx(Result, UTASSERT_CASETYPE_FAILURE, File, Line, "String: \'%s\' == \'%s\'", ScrubbedString1,
                      ScrubbedString2);
}
