static int alloc(void)
{
    void *ptr;
    FILE *log = fopen("/tmp/logs/597.txt", "a");

    if (log) {
        fprintf(log, "alloc: entered\n");
        fflush(log);
    }

    ptr = metal_allocate_memory(1000);

    if (log) {
        fprintf(log, "alloc: metal_allocate_memory returned ptr=%p\n", ptr);
        fflush(log);
    }

    if (!ptr) {
        int err = errno;
        if (log) {
            fprintf(log, "alloc: allocation failed, errno=%d; about to call metal_log\n", err);
            fflush(log);
        }

        /* Preserve original behavior: call metal_log as in the original code. */
        metal_log(METAL_LOG_DEBUG, "failed to allocate memory\n");

        if (log) {
            fprintf(log, "alloc: metal_log invoked; returning errno=%d\n", err);
            fclose(log);
        }

        return err;
    }

    if (log) {
        fprintf(log, "alloc: freeing allocated memory ptr=%p\n", ptr);
        fflush(log);
    }

    metal_free_memory(ptr);

    if (log) {
        fprintf(log, "alloc: freed memory; returning 0\n");
        fclose(log);
    }

    return 0;
}
