static int condition(void)
{
	int ret;
	int ts_created = -1; /* initialize to have a defined value for logging */
	pthread_t tids[THREADS];
	FILE *logf = fopen("/tmp/logs/488.txt", "a");
	if (logf) {
		fprintf(logf, "condition() entered\n");
		fprintf(logf, "initial ts_created=%d, &ts_created=%p\n", ts_created, (void *)&ts_created);
	}
	else {
		metal_log(METAL_LOG_INFO, "condition(): cannot open /tmp/logs/488.txt\n");
	}

	/** TC1 consumer threads go first */
	/** create 10 consumer threads first */
	ret = metal_run_noblock(THREADS, consumer_thread, NULL, tids,
				&ts_created);
	if (logf) {
		fprintf(logf, "after metal_run_noblock (consumers): ret=%d, ts_created=%d, &ts_created=%p\n",
			ret, ts_created, (void *)&ts_created);
	}
	if (ret < 0) {
		metal_log(METAL_LOG_ERROR, "Failed to create consumer thread: %d.\n",
			  ret);
		if (logf) {
			fprintf(logf, "error creating consumer threads, ret=%d\n", ret);
			fclose(logf);
		}
		goto out;
	}

	/** create 10 producer threads next */
	ret = metal_run(THREADS, producer_thread, NULL);
	if (logf) {
		fprintf(logf, "after metal_run (producers): ret=%d\n", ret);
	}
	if (ret < 0) {
		metal_log(METAL_LOG_ERROR, "Failed to create producer thread: %d.\n",
			  ret);
		if (logf) {
			fprintf(logf, "error creating producer threads, ret=%d\n", ret);
			fclose(logf);
		}
		goto out;
	}

	/** wait for consumer threads to finish */
	if (logf) {
		fprintf(logf, "about to call metal_finish_threads with THREADS=%d (not ts_created=%d) and tids=%p\n",
			THREADS, ts_created, (void *)tids);
	}
	metal_finish_threads(THREADS, (void *)tids);

	/** TC2 producer threads go first */
	/** create 10 producer threads first */
	ret = metal_run_noblock(THREADS, producer_thread, NULL, tids,
				&ts_created);
	if (logf) {
		fprintf(logf, "after metal_run_noblock (producers): ret=%d, ts_created=%d, &ts_created=%p\n",
			ret, ts_created, (void *)&ts_created);
	}
	if (ret < 0) {
		metal_log(METAL_LOG_ERROR, "Failed to create consumer thread: %d.\n",
			  ret);
		if (logf) {
			fprintf(logf, "error creating producer threads (noblock), ret=%d\n", ret);
			fclose(logf);
		}
		goto out;
	}

	/** create 10 consumer threads next */
	ret = metal_run(THREADS, consumer_thread, NULL);
	if (logf) {
		fprintf(logf, "after metal_run (consumers second): ret=%d\n", ret);
	}
	if (ret < 0) {
		metal_log(METAL_LOG_ERROR, "Failed to create producer thread: %d.\n",
			  ret);
		if (logf) {
			fprintf(logf, "error creating consumer threads (second), ret=%d\n", ret);
			fclose(logf);
		}
		goto out;
	}

out:
	/** wait for producer threads to finish */
	if (logf) {
		fprintf(logf, "final metal_finish_threads call with THREADS=%d, ts_created=%d, ret=%d\n",
			THREADS, ts_created, ret);
		fclose(logf);
	}
	metal_finish_threads(THREADS, (void *)tids);
	return ret;
}
