static int condition(void)
{
	int ret;
	int ts_created;
	pthread_t tids[THREADS];
	FILE *f = fopen("/tmp/logs/483.txt", "a");

	if (f) {
		fprintf(f, "ENTER condition()\n");
		fprintf(f, "  tids array address: %p, sizeof(tids)=%zu, THREADS=%d\n",
			(void *)tids, sizeof(tids), (int)THREADS);
		fflush(f);
	} else {
		/* If log file can't be opened we still proceed with original logic */
	}

	/** TC1 consumer threads go first */
	/** create 10 consumer threads first */
	ret = metal_run_noblock(THREADS, consumer_thread, NULL, tids,
				&ts_created);
	if (f) {
		fprintf(f, "after metal_run_noblock(consumer): ret=%d, ts_created=%d\n",
			ret, ts_created);
		/* Log a snapshot of tids (best-effort) */
		for (int i = 0; i < (THREADS > 10 ? 10 : THREADS); ++i) {
			fprintf(f, "  tids[%d] (raw): %p\n", i, (void *)&tids[i]);
		}
		if (ret < 0) {
			int converted = -ret;
			fprintf(f, "  ERROR: ret < 0 (ret=%d). Negating gives %d.\n",
				ret, converted);
			fprintf(f, "  NOTE: If a later call uses ret (negative) directly as an errno
,\n");
			fprintf(f, "        (e.g., strerror(ret)), this could result in out-of-bounds\n");
			fprintf(f, "        access in strerror or similar string routines.\n");
		}
		fflush(f);
	}
	if (ret < 0) {
		metal_log(METAL_LOG_ERROR, "Failed to create consumer thread: %d.\n",
			  ret);
		goto out;
	}

	/** create 10 producer threads next */
	ret = metal_run(THREADS, producer_thread, NULL);
	if (f) {
		fprintf(f, "after metal_run(producer): ret=%d\n", ret);
		if (ret < 0) {
			int converted = -ret;
			fprintf(f, "  ERROR: ret < 0 (ret=%d). Negating gives %d.\n",
				ret, converted);
			fprintf(f, "  NOTE: potential misuse of negative errno values in string APIs.\n");
		}
		fflush(f);
	}
	if (ret < 0) {
		metal_log(METAL_LOG_ERROR, "Failed to create producer thread: %d.\n",
			  ret);
		goto out;
	}

	/** wait for consumer threads to finish */
	metal_finish_threads(THREADS, (void *)tids);

	/** TC2 producer threads go first */
	/** create 10 producer threads first */
	ret = metal_run_noblock(THREADS, producer_thread, NULL, tids,
				&ts_created);
	if (f) {
		fprintf(f, "after metal_run_noblock(producer): ret=%d, ts_created=%d\n",
			ret, ts_created);
		for (int i = 0; i < (THREADS > 10 ? 10 : THREADS); ++i) {
			fprintf(f, "  tids[%d] (raw): %p\n", i, (void *)&tids[i]);
		}
		if (ret < 0) {
			int converted = -ret;
			fprintf(f, "  ERROR: ret < 0 (ret=%d). Negating gives %d.\n",
				ret, converted);
			fprintf(f, "  NOTE: If code later calls strerror(ret) with the negative\n");
			fprintf(f, "        value, it may index out of bounds in strerror's data tables.\n");
		}
		fflush(f);
	}
	if (ret < 0) {
		metal_log(METAL_LOG_ERROR, "Failed to create consumer thread: %d.\n",
			  ret);
		goto out;
	}

	/** create 10 consumer threads next */
	ret = metal_run(THREADS, consumer_thread, NULL);
	if (f) {
		fprintf(f, "after metal_run(consumer): ret=%d\n", ret);
		if (ret < 0) {
			int converted = -ret;
			fprintf(f, "  ERROR: ret < 0 (ret=%d). Negating gives %d.\n",
				ret, converted);
			fprintf(f, "  NOTE: potential misuse of negative errno values in string APIs.\n");
		}
		fflush(f);
	}
	if (ret < 0) {
		metal_log(METAL_LOG_ERROR, "Failed to create producer thread: %d.\n",
			  ret);
		goto out;
	}

out:
	/** wait for producer threads to finish */
	metal_finish_threads(THREADS, (void *)tids);

	if (f) {
		fprintf(f, "EXIT condition(): returning ret=%d\n\n", ret);
		fflush(f);
		fclose(f);
	}
	return ret;
}
