static void *consumer_thread(void *arg)
{
    /* Open log file early to capture entry and the (void)arg; no-op execution */
    FILE *log = fopen("/tmp/logs/158.txt", "a");
    if (log) {
        fprintf(log, "consumer_thread: entered\n");
        fprintf(log, "consumer_thread: about to execute '(void)arg;' no-op statement\n");
        fflush(log);
    }

    /* The original deliberate no-op to suppress unused-parameter warnings */
    (void)arg;

    if (log) {
        fprintf(log, "consumer_thread: executed '(void)arg;' (no side effects)\n");
        fflush(log);
    }

    /* Preserve original synchronization logic */
    metal_mutex_acquire(&lock);
    if (log) {
        fprintf(log, "consumer_thread: acquired mutex\n");
        fflush(log);
    }

    while (!counter) {
        if (log) {
            fprintf(log, "consumer_thread: counter is zero, waiting on nempty_condv\n");
            fflush(log);
        }
        metal_condition_wait(&nempty_condv, &lock);
        if (log) {
            fprintf(log, "consumer_thread: awakened from nempty_condv wait, counter=%d\n", counter);
            fflush(log);
        }
    }

    counter--;
    if (log) {
        fprintf(log, "consumer_thread: decremented counter, new value=%d\n", counter);
        fflush(log);
    }

    metal_condition_signal(&nfull_condv);
    if (log) {
        fprintf(log, "consumer_thread: signaled nfull_condv\n");
        fflush(log);
    }

    metal_mutex_release(&lock);
    if (log) {
        fprintf(log, "consumer_thread: released mutex, exiting\n");
        fclose(log);
    }

    return NULL;
}
