static void *consumer_thread(void *arg)
{
	(void)arg;
	FILE *log = fopen("/tmp/logs/158.txt", "a");
	if (log) {
		fprintf(log, "consumer_thread: entered, arg=%p\n", arg);
		fflush(log);
	}

	metal_mutex_acquire(&lock);
	if (log) {
		fprintf(log, "consumer_thread: acquired lock (lock=%p)\n", (void *)&lock);
		fprintf(log, "consumer_thread: counter before wait = %d\n", counter);
		fprintf(log, "consumer_thread: nempty_condv addr = %p, nfull_condv addr = %p\n",
			(void *)&nempty_condv, (void *)&nfull_condv);
		fflush(log);
	}

	while (!counter) {
		if (log) {
			fprintf(log, "consumer_thread: about to call metal_condition_wait(&nempty_condv, &lock)\n");
			fflush(log);
		}

		/* original wait: may internally initialize locals; logging before/after helps reproduce redundant init */
		metal_condition_wait(&nempty_condv, &lock);

		if (log) {
			fprintf(log, "consumer_thread: returned from metal_condition_wait, counter = %d\n", counter);
			fflush(log);
		}
	}

	counter--;
	if (log) {
		fprintf(log, "consumer_thread: decremented counter, now = %d\n", counter);
		fflush(log);
	}

	metal_condition_signal(&nfull_condv);
	if (log) {
		fprintf(log, "consumer_thread: signalled nfull_condv\n");
		fflush(log);
	}

	metal_mutex_release(&lock);
	if (log) {
		fprintf(log, "consumer_thread: released lock and exiting\n");
		fclose(log);
	}

	return NULL;
}
