static int irq(void)
{
	int rc = 0;
	char *err_msg="";
	enum metal_log_level mll = metal_get_log_level();
	int i, tst_irq[2];
	FILE *logf = fopen("/tmp/logs/87.txt", "a");

	/* Minimal entry log so callers/test harness can observe execution */
	if (logf) {
		fprintf(logf, "ENTRY: %s called; saved_log_level=%d\n", __func__, (int)mll);
	}

	/* Do not show LOG_ERROR or LOG_DEBUG for expected fail case */
	metal_set_log_level(METAL_LOG_CRITICAL);
	if (logf) {
		fprintf(logf, "ACTION: set log level to METAL_LOG_CRITICAL\n");
	}

	for (i = 0; i < 2; i++) {
		/* we only want to test the lib API, so create 'virtual' IRQs */
		tst_irq[i] = eventfd(0,0);
		if (logf) {
			fprintf(logf, "LOOP: created eventfd for i=%d -> fd=%d\n", i, tst_irq[i]);
		}
		metal_log(METAL_LOG_DEBUG, "%s: irq %d associated with fd %d\n",
			  __func__, i, tst_irq[i]);
		if (logf) {
			fprintf(logf, "CALL: metal_log(METAL_LOG_DEBUG, \"%s: irq %d associated with fd %d\\n\", __func__, %d, %d) invoked\n",
				__func__, i, tst_irq[i], i, tst_irq[i]);
		}
	}

	rc = metal_irq_register(tst_irq[0], irq_handler, (void *)1);
	if (logf) {
		fprintf(logf, "CALL: metal_irq_register(fd=%d, handler=%p, arg=%p) returned %d\n",
			 tst_irq[0], (void *)irq_handler, (void *)1, rc);
	}
	if (rc) {
		err_msg = "register irq 0 fail drv_id 1\n";
		if (logf) fprintf(logf, "ERROR: %s rc=%d\n", err_msg, rc);
		goto out;
	}
	rc = metal_irq_register(tst_irq[1], irq_handler, (void *)1);
	if (logf) {
		fprintf(logf, "CALL: metal_irq_register(fd=%d, handler=%p, arg=%p) returned %d\n",
			 tst_irq[1], (void *)irq_handler, (void *)1, rc);
	}
	if (rc) {
		err_msg = "register irq 1 fail drv_id 1\n";
		if (logf) fprintf(logf, "ERROR: %s rc=%d\n", err_msg, rc);
		goto out;
	}

	metal_irq_unregister(tst_irq[0]);
	if (logf) fprintf(logf, "CALL: metal_irq_unregister(fd=%d)\n", tst_irq[0]);
	rc = metal_irq_register(tst_irq[0], irq_handler, (void *)1);
	if (logf) fprintf(logf, "CALL: metal_irq_register(fd=%d, handler=%p, arg=%p) returned %d\n",
			 tst_irq[0], (void *)irq_handler, (void *)1, rc);
	if (rc) {
		err_msg = "register irq 0 after unregistering failed\n";
		if (logf) fprintf(logf, "ERROR: %s rc=%d\n", err_msg, rc);
		goto out;
	}
	metal_irq_unregister(tst_irq[0]);
	metal_irq_unregister(tst_irq[1]);
	if (logf) {
		fprintf(logf, "CALL: metal_irq_unregister(fd=%d) and unregister(fd=%d) completed\n", tst_irq[0], tst_irq[1]);
	}

out:
	for (i = 0; i < 2; i++) {
		close(tst_irq[i]);
		if (logf) fprintf(logf, "CLEANUP: closed fd for i=%d (fd=%d)\n", i, tst_irq[i]);
	}
	metal_set_log_level(mll);
	if (logf) fprintf(logf, "ACTION: restored log level to %d\n", (int)mll);
	if ((err_msg[0] != '\0') && (!rc))
		rc = -EINVAL;
	if (logf) {
		fprintf(logf, "EXIT: err_msg=\"%s\", final_rc=%d\n", err_msg, rc);
		/* Add reported-violation context so other agents/tests can find evidence */
		fprintf(logf, "NOTE: external agent reported that macro 'metal_log' uses GCC statement-expression '({ ... })' and GNU preprocessor extension '##__VA_ARGS__'\n");
		fclose(logf);
	}
	if (rc) metal_log(METAL_LOG_ERROR, "%s", err_msg);
	return rc;
}
