static int irq(void)
{
	int rc = 0;
	char *err_msg="";
	enum metal_log_level mll= metal_get_log_level();
	int i, tst_irq[2];
	FILE *lf = NULL;
	int log_to_file = 0;

	/* Try to open log file for appending; if failed, fall back to metal_log */
	lf = fopen("/tmp/logs/86.txt", "a");
	if (lf) log_to_file = 1;

	/* Minimal entry probe */
	if (log_to_file) {
		fprintf(lf, "ENTRY: %s: initial rc=%d err_msg[0]='%c' mll=%d\n",
			__func__, rc, err_msg[0] ? err_msg[0] : '\\0', (int)mll);
		fflush(lf);
	} else {
		metal_log(METAL_LOG_DEBUG, "%s: log file unavailable, initial rc=%d err_msg[0]=%c mll=%d\n",
			__func__, rc, err_msg[0] ? err_msg[0] : '\\0', (int)mll);
	}

	/* Do not show LOG_ERROR or LOG_DEBUG for expected fail case */
	metal_set_log_level(METAL_LOG_CRITICAL);

	for (i = 0; i < 2; i++) {
		/* we only want to test the lib API, so create 'virtual' IRQs */
		tst_irq[i] = eventfd(0,0);
		if (log_to_file) {
			fprintf(lf, "%s: created tst_irq[%d]=%d\n", __func__, i, tst_irq[i]);
			fflush(lf);
		} else {
			metal_log(METAL_LOG_DEBUG, "%s: created tst_irq[%d]=%d\n", __func__, i, tst_irq[i]);
		}
	}

	rc = metal_irq_register(tst_irq[0], irq_handler, (void *)1);
	if (log_to_file) {
		fprintf(lf, "%s: after register 0 rc=%d\n", __func__, rc);
		fflush(lf);
	} else {
		metal_log(METAL_LOG_DEBUG, "%s: after register 0 rc=%d\n", __func__, rc);
	}
	if (rc) {
		err_msg = "register irq 0 fail drv_id 1\n";
		if (log_to_file) {
			fprintf(lf, "%s: error set err_msg=\"%s\" rc=%d -> goto out\n", __func__, err_msg, rc);
			fflush(lf);
		} else {
			metal_log(METAL_LOG_DEBUG, "%s: error set err_msg=\"%s\" rc=%d -> goto out\n", __func__, err_msg, rc);
		}
		goto out;
	}
	rc = metal_irq_register(tst_irq[1], irq_handler, (void *)1);
	if (log_to_file) {
		fprintf(lf, "%s: after register 1 rc=%d\n", __func__, rc);
		fflush(lf);
	} else {
		metal_log(METAL_LOG_DEBUG, "%s: after register 1 rc=%d\n", __func__, rc);
	}
	if (rc) {
		err_msg = "register irq 1 fail drv_id 1\n";
		if (log_to_file) {
			fprintf(lf, "%s: error set err_msg=\"%s\" rc=%d -> goto out\n", __func__, err_msg, rc);
			fflush(lf);
		} else {
			metal_log(METAL_LOG_DEBUG, "%s: error set err_msg=\"%s\" rc=%d -> goto out\n", __func__, err_msg, rc);
		}
		goto out;
	}

	metal_irq_unregister(tst_irq[0]);
	rc = metal_irq_register(tst_irq[0], irq_handler, (void *)1);
	if (log_to_file) {
		fprintf(lf, "%s: after unregister/register 0 rc=%d\n", __func__, rc);
		fflush(lf);
	} else {
		metal_log(METAL_LOG_DEBUG, "%s: after unregister/register 0 rc=%d\n", __func__, rc);
	}
	if (rc) {
		err_msg = "register irq 0 after unregistering failed\n";
		if (log_to_file) {
			fprintf(lf, "%s: error set err_msg=\"%s\" rc=%d -> goto out\n", __func__, err_msg, rc);
			fflush(lf);
		} else {
			metal_log(METAL_LOG_DEBUG, "%s: error set err_msg=\"%s\" rc=%d -> goto out\n", __func__, err_msg, rc);
		}
		goto out;
	}
	metal_irq_unregister(tst_irq[0]);
	metal_irq_unregister(tst_irq[1]);

out:
	for (i = 0; i < 2; i++) {
		close(tst_irq[i]);
		if (log_to_file) {
			fprintf(lf, "%s: closed tst_irq[%d]=%d\n", __func__, i, tst_irq[i]);
			fflush(lf);
		} else {
			metal_log(METAL_LOG_DEBUG, "%s: closed tst_irq[%d]=%d\n", __func__, i, tst_irq[i]);
		}
	}
	metal_set_log_level(mll);

	/* Log the values relevant to the potentially unreachable branch */
	if (log_to_file) {
		fprintf(lf, "%s: at out: err_msg=\"%s\" err_msg[0]='%c' rc=%d\n",
			__func__, err_msg, err_msg[0] ? err_msg[0] : '\\0', rc);
		fflush(lf);
	} else {
		metal_log(METAL_LOG_DEBUG, "%s: at out: err_msg=\"%s\" err_msg[0]='%c' rc=%d\n",
			__func__, err_msg, err_msg[0] ? err_msg[0] : '\\0', rc);
	}

	/* Evaluate the condition and log decision before possibly changing rc */
	if (log_to_file) {
		fprintf(lf, "%s: condition (err_msg[0] != '\\0') = %d, (!rc) = %d\n",
			__func__, (err_msg[0] != '\\0') ? 1 : 0, (!rc) ? 1 : 0);
		fflush(lf);
	} else {
		metal_log(METAL_LOG_DEBUG, "%s: condition (err_msg[0] != '\\0') = %d, (!rc) = %d\n",
			__func__, (err_msg[0] != '\\0') ? 1 : 0, (!rc) ? 1 : 0);
	}

	if ((err_msg[0] != '\0') && (!rc))
		rc = -EINVAL;

	if (log_to_file) {
		fprintf(lf, "%s: after possibly setting - final rc=%d err_msg=\"%s\"\n",
			__func__, rc, err_msg);
		fflush(lf);
		fclose(lf);
	} else {
		metal_log(METAL_LOG_DEBUG, "%s: after possibly setting - final rc=%d err_msg=\"%s\"\n",
			__func__, rc, err_msg);
	}

	if (rc) metal_log(METAL_LOG_ERROR, "%s", err_msg);
	return rc;
}
