static int irq(void)
{
	int rc = 0;
	char *err_msg="";
	enum metal_log_level mll = metal_get_log_level();
	int i, tst_irq[2];
	FILE *logf = NULL;

	/* Try to create log directory if it does not exist (best-effort). */
	(void)mkdir("/tmp/logs", 0777);
	logf = fopen("/tmp/logs/88.txt", "a");
	if (logf) {
		fprintf(logf, "ENTRY: irq() called\n");
		fprintf(logf, "STATE: initial_log_level=%d\n", (int)mll);
		fflush(logf);
	}

	/* Do not show LOG_ERROR or LOG_DEBUG for expected fail case */
	metal_set_log_level(METAL_LOG_CRITICAL);
	if (logf) {
		fprintf(logf, "ACTION: set log level to METAL_LOG_CRITICAL\n");
		fflush(logf);
	}

	for (i = 0; i < 2; i++) {
		/* we only want to test the lib API, so create 'virtual' IRQs */
		tst_irq[i] = eventfd(0,0);
		metal_log(METAL_LOG_DEBUG, "%s: irq %d associated with fd %d\n",
			  __func__, i, tst_irq[i]);
		if (logf) {
			fprintf(logf, "ACTION: created tst_irq[%d]=%d\n", i, tst_irq[i]);
			fflush(logf);
		}
	}

	/* Register first handler */
	rc = metal_irq_register(tst_irq[0], irq_handler, (void *)1);
	if (logf) {
		fprintf(logf, "CALL: metal_irq_register(tst_irq[0]=%d, irq_handler, priv=(void*)1) returned rc=%d\n",
			 tst_irq[0], rc);
		/* Record whether a handler symbol appears present and the priv value used for registration. */
		fprintf(logf, "INFO: handler_present=%d priv_value=%p\n",
			(irq_handler != (void *)0) ? 1 : 0, (void *)1);
		fflush(logf);
	}
	if (rc) {
		err_msg = "register irq 0 fail drv_id 1\n";
		goto out;
	}

	/* Register second handler */
	rc = metal_irq_register(tst_irq[1], irq_handler, (void *)1);
	if (logf) {
		fprintf(logf, "CALL: metal_irq_register(tst_irq[1]=%d, irq_handler, priv=(void*)1) returned rc=%d\n",
			 tst_irq[1], rc);
		fflush(logf);
	}
	if (rc) {
		err_msg = "register irq 1 fail drv_id 1\n";
		goto out;
	}

	/* Unregister and re-register to exercise that path */
	metal_irq_unregister(tst_irq[0]);
	if (logf) {
		fprintf(logf, "CALL: metal_irq_unregister(tst_irq[0]=%d)\n", tst_irq[0]);
		fflush(logf);
	}

	rc = metal_irq_register(tst_irq[0], irq_handler, (void *)1);
	if (logf) {
		fprintf(logf, "CALL: metal_irq_register(tst_irq[0] after unregister, priv=(void*)1) returned rc=%d\n", rc);
		fflush(logf);
	}
	if (rc) {
		err_msg = "register irq 0 after unregistering failed\n";
		goto out;
	}
	metal_irq_unregister(tst_irq[0]);
	metal_irq_unregister(tst_irq[1]);
	if (logf) {
		fprintf(logf, "CALL: metal_irq_unregister(tst_irq[0]) and metal_irq_unregister(tst_irq[1]) completed\n");
		fflush(logf);
	}

out:
	for (i = 0; i < 2; i++) {
		/* Ensure we log the close attempts and the fd values */
		if (logf) fprintf(logf, "ACTION: closing tst_irq[%d]=%d\n", i, tst_irq[i]);
		close(tst_irq[i]);
	}
	metal_set_log_level(mll);
	if (logf) {
		fprintf(logf, "ACTION: restored log level to %d\n", (int)mll);
	}
	if ((err_msg[0] != '\0') && (!rc))
		rc = -EINVAL;
	if (rc) metal_log(METAL_LOG_ERROR, "%s", err_msg);
	if (logf) {
		fprintf(logf, "EXIT: irq() rc=%d err_msg='%s'\n", rc, err_msg);
		fclose(logf);
	}
	return rc;
}
