static int irq(void)
{
	int rc = 0;
	char *err_msg="";
	enum metal_log_level mll = metal_get_log_level();
	int i, tst_irq[2];
	/* Logging probe file to capture control flow/state for the violation scenario */
	FILE *logf = fopen("/tmp/logs/85.txt", "a");
	if (logf) {
		fprintf(logf, "ENTER irq: initial_log_level=%d\n", (int)mll);
		fflush(logf);
	}

	/* Do not show LOG_ERROR or LOG_DEBUG for expected fail case */
	metal_set_log_level(METAL_LOG_CRITICAL);

	for (i = 0; i < 2; i++) {
		/* we only want to test the lib API, so create 'virtual' IRQs */
		tst_irq[i] = eventfd(0,0);
		metal_log(METAL_LOG_DEBUG, "%s: irq %d associated with fd %d\n",
			  __func__, i, tst_irq[i]);
		if (logf) {
			fprintf(logf, "created virtual irq: index=%d fd=%d\n", i, tst_irq[i]);
			fflush(logf);
		}
	}

	/* Log the fact that we are about to register with a non-const void* arg (the violation scenario) */
	if (logf) {
		fprintf(logf, "about to call metal_irq_register for irq 0: fd=%d handler=%p arg=%p\n",
			 tst_irq[0], (void *)irq_handler, (void *)1);
		fflush(logf);
	}
	rc = metal_irq_register(tst_irq[0], irq_handler, (void *)1);
	if (logf) {
		fprintf(logf, "metal_irq_register returned %d for irq 0\n", rc);
		fflush(logf);
	}
	if (rc) {
		err_msg = "register irq 0 fail drv_id 1\n";
		goto out;
	}

	if (logf) {
		fprintf(logf, "about to call metal_irq_register for irq 1: fd=%d handler=%p arg=%p\n",
			 tst_irq[1], (void *)irq_handler, (void *)1);
		fflush(logf);
	}
	rc = metal_irq_register(tst_irq[1], irq_handler, (void *)1);
	if (logf) {
		fprintf(logf, "metal_irq_register returned %d for irq 1\n", rc);
		fflush(logf);
	}
	if (rc) {
		err_msg = "register irq 1 fail drv_id 1\n";
		goto out;
	}

	if (logf) {
		fprintf(logf, "about to unregister irq 0 (fd=%d)\n", tst_irq[0]);
		fflush(logf);
	}
	metal_irq_unregister(tst_irq[0]);

	if (logf) {
		fprintf(logf, "about to re-register irq 0 with same non-const arg (fd=%d)\n", tst_irq[0]);
		fflush(logf);
	}
	rc = metal_irq_register(tst_irq[0], irq_handler, (void *)1);
	if (logf) {
		fprintf(logf, "metal_irq_register returned %d for irq 0 (after unregister)\n", rc);
		fflush(logf);
	}
	if (rc) {
		err_msg = "register irq 0 after unregistering failed\n";
		goto out;
	}
	metal_irq_unregister(tst_irq[0]);
	metal_irq_unregister(tst_irq[1]);

out:
	for (i = 0; i < 2; i++) {
		close(tst_irq[i]);
		if (logf) {
			fprintf(logf, "closed fd for irq %d: fd=%d\n", i, tst_irq[i]);
			fflush(logf);
		}
	}
	metal_set_log_level(mll);
	if (logf) {
		fprintf(logf, "restored_log_level=%d final_rc=%d err_msg=%s\n",
			 (int)mll, rc, (err_msg && err_msg[0]) ? err_msg : "(none)");
		fflush(logf);
		fclose(logf);
	}
	if ((err_msg[0] != '\0') && (!rc))
		rc = -EINVAL;
	if (rc) metal_log(METAL_LOG_ERROR, "%s", err_msg);
	return rc;
}
