static int irq(void)
{
	int rc = 0;
	char *err_msg="";
	enum metal_log_level mll= metal_get_log_level();
	int i, tst_irq[2];
	FILE *log = NULL;

	/* Open probe log file for append. If it fails, we continue without logging. */
	log = fopen("/tmp/logs/85.txt", "a");
	if (log) {
		fprintf(log, "ENTRY: irq() called\n");
		fprintf(log, "initial mll=%d, err_msg=%p (\"%s\"), rc=%d\n",
			(int)mll, (void *)err_msg, err_msg, rc);
		fflush(log);
	}

	/* Do not show LOG_ERROR or LOG_DEBUG for expected fail case */
	metal_set_log_level(METAL_LOG_CRITICAL);
	if (log) {
		fprintf(log, "metal_set_log_level(METAL_LOG_CRITICAL) done\n");
		fflush(log);
	}

	for (i = 0; i < 2; i++) {
		/* we only want to test the lib API, so create 'virtual' IRQs */
		tst_irq[i] = eventfd(0,0);
		if (log) {
			fprintf(log, "eventfd: i=%d -> fd=%d\n", i, tst_irq[i]);
			fflush(log);
		}
		metal_log(METAL_LOG_DEBUG, "%s: irq %d associated with fd %d\n",
			  __func__, i, tst_irq[i]);
	}

	rc = metal_irq_register(tst_irq[0], irq_handler, (void *)1);
	if (log) {
		fprintf(log, "metal_irq_register(fd=%d) returned rc=%d\n", tst_irq[0], rc);
		fflush(log);
	}
	if (rc) {
		err_msg = "register irq 0 fail drv_id 1\n";
		if (log) {
			fprintf(log, "ERROR PATH: set err_msg=%p (\"%s\")\n",
				(void *)err_msg, err_msg);
			fflush(log);
		}
		goto out;
	}
	rc = metal_irq_register(tst_irq[1], irq_handler, (void *)1);
	if (log) {
		fprintf(log, "metal_irq_register(fd=%d) returned rc=%d\n", tst_irq[1], rc);
		fflush(log);
	}
	if (rc) {
		err_msg = "register irq 1 fail drv_id 1\n";
		if (log) {
			fprintf(log, "ERROR PATH: set err_msg=%p (\"%s\")\n",
				(void *)err_msg, err_msg);
			fflush(log);
		}
		goto out;
	}

	metal_irq_unregister(tst_irq[0]);
	if (log) {
		fprintf(log, "metal_irq_unregister(fd=%d) called\n", tst_irq[0]);
		fflush(log);
	}
	rc = metal_irq_register(tst_irq[0], irq_handler, (void *)1);
	if (log) {
		fprintf(log, "metal_irq_register(after unregister) fd=%d returned rc=%d\n", tst_irq[0], rc);
		fflush(log);
	}
	if (rc) {
		err_msg = "register irq 0 after unregistering failed\n";
		if (log) {
			fprintf(log, "ERROR PATH: set err_msg=%p (\"%s\")\n",
				(void *)err_msg, err_msg);
			fflush(log);
		}
		goto out;
	}
	metal_irq_unregister(tst_irq[0]);
	metal_irq_unregister(tst_irq[1]);
	if (log) {
		fprintf(log, "unregistered both irqs fd0=%d fd1=%d\n", tst_irq[0], tst_irq[1]);
		fflush(log);
	}

out:
	for (i = 0; i < 2; i++) {
		close(tst_irq[i]);
		if (log) {
			fprintf(log, "closed fd[%d]=%d\n", i, tst_irq[i]);
			fflush(log);
		}
	}
	metal_set_log_level(mll);
	if (log) {
		fprintf(log, "metal_set_log_level(mll=%d) restored\n", (int)mll);
		fflush(log);
	}
	if ((err_msg[0] != '\0') && (!rc))
		rc = -EINVAL;
	if (log) {
		fprintf(log, "final state: rc=%d, err_msg=%p (\"%s\"), err_msg[0]=%c\n",
			rc, (void *)err_msg, err_msg, (err_msg[0] != '\0') ? err_msg[0] : '0');
		fflush(log);
	}
	if (rc) metal_log(METAL_LOG_ERROR, "%s", err_msg);
	if (log) fclose(log);
	return rc;
}
