static int irq(void)
{
	int rc = 0;
	char *err_msg="";
	enum metal_log_level mll= metal_get_log_level();
	int i, tst_irq[2];
	FILE *logf = NULL;
	void *probe_arg = (void *)1; /* preserve original literal used at call sites */

	/* Try to open log file for appending diagnostic traces */
	logf = fopen("/tmp/logs/85.txt", "a");
	if (logf) {
		fprintf(logf, "irq: entered\n");
		fprintf(logf, "irq: initial metal log level = %d\n", (int)mll);
		fflush(logf);
	}

	/* Do not show LOG_ERROR or LOG_DEBUG for expected fail case */
	metal_set_log_level(METAL_LOG_CRITICAL);
	if (logf) {
		fprintf(logf, "irq: set metal log level to METAL_LOG_CRITICAL\n");
		fflush(logf);
	}

	for (i = 0; i < 2; i++) {
		/* we only want to test the lib API, so create 'virtual' IRQs */
		tst_irq[i] = eventfd(0,0);
		metal_log(METAL_LOG_DEBUG, "%s: irq %d associated with fd %d\n",
			  __func__, i, tst_irq[i]);
		if (logf) {
			fprintf(logf, "irq: created virtual irq index=%d fd=%d\n", i, tst_irq[i]);
			fflush(logf);
		}
	}

	if (logf) fprintf(logf, "irq: about to register irq[0] with arg=%p\n", probe_arg);
	fflush(logf);
	rc = metal_irq_register(tst_irq[0], irq_handler, probe_arg);
	if (logf) fprintf(logf, "irq: metal_irq_register(fd=%d) returned %d\n", tst_irq[0], rc);
	if (rc) {
		err_msg = "register irq 0 fail drv_id 1\n";
		if (logf) fprintf(logf, "irq: error - %s\n", err_msg);
		goto out;
	}

	if (logf) fprintf(logf, "irq: about to register irq[1] with arg=%p\n", probe_arg);
	fflush(logf);
	rc = metal_irq_register(tst_irq[1], irq_handler, probe_arg);
	if (logf) fprintf(logf, "irq: metal_irq_register(fd=%d) returned %d\n", tst_irq[1], rc);
	if (rc) {
		err_msg = "register irq 1 fail drv_id 1\n";
		if (logf) fprintf(logf, "irq: error - %s\n", err_msg);
		goto out;
	}

	if (logf) fprintf(logf, "irq: unregistering irq[0] fd=%d\n", tst_irq[0]);
	fflush(logf);
	metal_irq_unregister(tst_irq[0]);

	if (logf) fprintf(logf, "irq: re-registering irq[0] with arg=%p\n", probe_arg);
	fflush(logf);
	rc = metal_irq_register(tst_irq[0], irq_handler, probe_arg);
	if (logf) fprintf(logf, "irq: metal_irq_register(fd=%d) returned %d (after unregister)\n", tst_irq[0], rc);
	if (rc) {
		err_msg = "register irq 0 after unregistering failed\n";
		if (logf) fprintf(logf, "irq: error - %s\n", err_msg);
		goto out;
	}
	if (logf) fprintf(logf, "irq: final unregisters for irq[0]=%d irq[1]=%d\n", tst_irq[0], tst_irq[1]);
	fflush(logf);
	metal_irq_unregister(tst_irq[0]);
	metal_irq_unregister(tst_irq[1]);

out:
	for (i = 0; i < 2; i++) {
		close(tst_irq[i]);
		if (logf) fprintf(logf, "irq: closed fd %d\n", tst_irq[i]);
	}
	metal_set_log_level(mll);
	if (logf) fprintf(logf, "irq: restored metal log level to %d\n", (int)mll);
	fflush(logf);
	if ((err_msg[0] != '\0') && (!rc))
		rc = -EINVAL;
	if (rc) metal_log(METAL_LOG_ERROR, "%s", err_msg);
	if (logf) {
		fprintf(logf, "irq: exiting with rc=%d err_msg='%s' probe_arg=%p\n", rc, err_msg, probe_arg);
		fclose(logf);
	}
	return rc;
}
