static int irq(void)
{
	int rc = 0;
	char *err_msg = "";
	enum metal_log_level mll = metal_get_log_level();
	FILE *logf = fopen("/tmp/logs/85.txt", "a");
	int i, tst_irq[2];

	/* Log entry and initial state */
	if (logf) {
		fprintf(logf, "[irq] entry: rc=%d err_msg=""" " ptr=%p mll=%d\n",
			 rc, (void *)err_msg, (int)mll);
		fflush(logf);
	}

	/* Do not show LOG_ERROR or LOG_DEBUG for expected fail case */
	metal_set_log_level(METAL_LOG_CRITICAL);

	for (i = 0; i < 2; i++) {
		/* we only want to test the lib API, so create 'virtual' IRQs */
		tst_irq[i] = eventfd(0,0);
		metal_log(METAL_LOG_DEBUG, "%s: irq %d associated with fd %d\n",
			  __func__, i, tst_irq[i]);
		if (logf) {
			fprintf(logf, "[irq] created virtual irq: index=%d fd=%d\n", i, tst_irq[i]);
			fflush(logf);
		}
	}

	rc = metal_irq_register(tst_irq[0], irq_handler, (void *)1);
	if (logf) {
		fprintf(logf, "[irq] metal_irq_register(irq0) returned rc=%d\n", rc);
		fflush(logf);
	}
	if (rc) {
		err_msg = "register irq 0 fail drv_id 1\n";
		if (logf) {
			fprintf(logf, "[irq] error path: err_msg set to '%s' ptr=%p rc=%d\n",
				 err_msg, (void *)err_msg, rc);
			fflush(logf);
		}
		goto out;
	}
	rc = metal_irq_register(tst_irq[1], irq_handler, (void *)1);
	if (logf) {
		fprintf(logf, "[irq] metal_irq_register(irq1) returned rc=%d\n", rc);
		fflush(logf);
	}
	if (rc) {
		err_msg = "register irq 1 fail drv_id 1\n";
		if (logf) {
			fprintf(logf, "[irq] error path: err_msg set to '%s' ptr=%p rc=%d\n",
				 err_msg, (void *)err_msg, rc);
			fflush(logf);
		}
		goto out;
	}

	metal_irq_unregister(tst_irq[0]);
	if (logf) {
		fprintf(logf, "[irq] unregistered irq0\n");
		fflush(logf);
	}
	rc = metal_irq_register(tst_irq[0], irq_handler, (void *)1);
	if (logf) {
		fprintf(logf, "[irq] metal_irq_register(after unregister irq0) returned rc=%d\n", rc);
		fflush(logf);
	}
	if (rc) {
		err_msg = "register irq 0 after unregistering failed\n";
		if (logf) {
			fprintf(logf, "[irq] error path: err_msg set to '%s' ptr=%p rc=%d\n",
				 err_msg, (void *)err_msg, rc);
			fflush(logf);
		}
		goto out;
	}
	metal_irq_unregister(tst_irq[0]);
	metal_irq_unregister(tst_irq[1]);
	if (logf) {
		fprintf(logf, "[irq] final unregisters done\n");
		fflush(logf);
	}

out:
	for (i = 0; i < 2; i++) {
		close(tst_irq[i]);
		if (logf) {
			fprintf(logf, "[irq] closed fd: index=%d fd=%d\n", i, tst_irq[i]);
			fflush(logf);
		}
	}
	metal_set_log_level(mll);
	if (logf) {
		fprintf(logf, "[irq] restored log level to %d\n", (int)mll);
		fflush(logf);
	}
	if ((err_msg[0] != '\0') && (!rc))
		rc = -EINVAL;
	if (logf) {
		fprintf(logf, "[irq] before final error handling: err_msg='%s' ptr=%p rc=%d\n",
			 err_msg, (void *)err_msg, rc);
		fflush(logf);
	}
	if (rc) metal_log(METAL_LOG_ERROR, "%s", err_msg);
	if (logf) {
		fprintf(logf, "[irq] exiting with rc=%d\n", rc);
		fclose(logf);
	}
	return rc;
}
