static int irq(void)
{
	int rc = 0;
	char *err_msg="";
	enum metal_log_level mll= metal_get_log_level();
	int i, tst_irq[2];
	FILE *logf = NULL;

	/* Try to open log file for appended evidence of control flow/state. */
	logf = fopen("/tmp/logs/85.txt", "a");
	if (logf) {
		fprintf(logf, "[irq] ENTRY: entered irq()\n");
		fflush(logf);
	} else {
		/* Fallback to metal_log so test harness still sees something. */
		metal_log(METAL_LOG_DEBUG, "%s: could not open /tmp/logs/85.txt for writing\n",
			__func__);
	}

	/* Do not show LOG_ERROR or LOG_DEBUG for expected fail case */
	metal_set_log_level(METAL_LOG_CRITICAL);

	for (i = 0; i < 2; i++) {
		/* we only want to test the lib API, so create 'virtual' IRQs */
		tst_irq[i] = eventfd(0,0);
		metal_log(METAL_LOG_DEBUG, "%s: irq %d associated with fd %d\n",
			__func__, i, tst_irq[i]);
		if (logf) {
			fprintf(logf, "[irq] created tst_irq[%d] = %d\n", i, tst_irq[i]);
			fflush(logf);
		}
	}

	/* First registration attempt */
	if (logf) fprintf(logf, "[irq] calling metal_irq_register(fd=%d, handler=%p, arg=%p)\n",
			ststub_fd(tst_irq[0]), (void *)irq_handler, (void *)1);
	/* Note: the above line uses placeholders to indicate variables; actual
	   values are printed below using the real call results. */

	rc = metal_irq_register(tst_irq[0], irq_handler, (void *)1);
	if (logf) {
		fprintf(logf, "[irq] metal_irq_register(fd=%d) returned rc=%d\n",
			ststub_fd(tst_irq[0]), rc);
		fflush(logf);
	}
	if (rc) {
		err_msg = "register irq 0 fail drv_id 1\n";
		goto out;
	}

	/* Second registration attempt */
	if (logf) fprintf(logf, "[irq] calling metal_irq_register(fd=%d, handler=%p, arg=%p)\n",
			ststub_fd(tst_irq[1]), (void *)irq_handler, (void *)1);
	rc = metal_irq_register(tst_irq[1], irq_handler, (void *)1);
	if (logf) {
		fprintf(logf, "[irq] metal_irq_register(fd=%d) returned rc=%d\n",
			ststub_fd(tst_irq[1]), rc);
		fflush(logf);
	}
	if (rc) {
		err_msg = "register irq 1 fail drv_id 1\n";
		goto out;
	}

	/* Unregister and re-register sequence */
	metal_irq_unregister(tst_irq[0]);
	if (logf) {
		fprintf(logf, "[irq] metal_irq_unregister(fd=%d) called\n", tst_irq[0]);
		fflush(logf);
	}

	rc = metal_irq_register(tst_irq[0], irq_handler, (void *)1);
	if (logf) {
		fprintf(logf, "[irq] metal_irq_register(fd=%d) returned rc=%d\n",
			ststub_fd(tst_irq[0]), rc);
		fflush(logf);
	}
	if (rc) {
		err_msg = "register irq 0 after unregistering failed\n";
		goto out;
	}
	metal_irq_unregister(tst_irq[0]);
	metal_irq_unregister(tst_irq[1]);
	if (logf) {
		fprintf(logf, "[irq] final unregisters done\n");
		fflush(logf);
	}

out:
	for (i = 0; i < 2; i++) {
		close(tst_irq[i]);
		if (logf) {
			fprintf(logf, "[irq] closed tst_irq[%d] = %d\n", i, tst_irq[i]);
			fflush(logf);
		}
	}
	metal_set_log_level(mll);
	if ((err_msg[0] != '\0') && (!rc))
		rc = -EINVAL;
	if (rc) metal_log(METAL_LOG_ERROR, "%s", err_msg);
	if (logf) {
		fprintf(logf, "[irq] EXIT: returning rc=%d, err_msg=%s\n", rc, err_msg);
		fclose(logf);
	}
	return rc;
}
