int metal_bus_find(const char *name, struct metal_bus **result)
{
	FILE *log = fopen("/tmp/logs/628.txt", "a");
	if (log) {
		/* Log entry into function and parameters. */
		fprintf(log, "ENTER metal_bus_find: name=%s result=%p\n",
			name ? name : "(null)", (void *)result);
	}

	struct metal_list *node;
	struct metal_bus *bus;

	metal_list_for_each(&_metal.common.bus_list, node) {
		bus = metal_container_of(node, struct metal_bus, node);
		if (log) {
			fprintf(log, "  iter: bus=%p bus->name=%s\n",
				(void *)bus, bus->name ? bus->name : "(null)");
		}

		/* Preserve original behavior: perform strcmp exactly as before. */
		int cmp = strcmp(bus->name, name);
		if (log) {
			fprintf(log, "    strcmp(bus->name, name) = %d\n", cmp);
		}

		if (cmp == 0 && result) {
			if (log) {
				fprintf(log, "    match and result non-NULL: setting *result=%p and returning 0\n", (void *)bus);
				fclose(log);
			}
			*result = bus;
			return 0;
		}
		if (cmp == 0 && !result) {
			if (log) {
				fprintf(log, "    match found but result is NULL: continuing search (no state change)\n");
			}
		}
	}

	if (log) {
		fprintf(log, "EXIT metal_bus_find: not found, returning -ENOENT\n");
		fclose(log);
	}
	return -ENOENT;
}
