int metal_bus_find(const char *name, struct metal_bus **result)
{
    FILE *logf = fopen("/tmp/logs/623.txt", "a");

    if (logf) {
        fprintf(logf, "ENTER metal_bus_find: this=%p name=%p result=%p\n",
                (void *) &metal_bus_find, (const void *) name, (void *) result);
        fflush(logf);
    }

    struct metal_list *node;
    struct metal_bus *bus;

    metal_list_for_each(&_metal.common.bus_list, node) {
        bus = metal_container_of(node, struct metal_bus, node);

        if (logf) {
            const void *bus_ptr = (const void *) bus;
            const void *bus_name_ptr = NULL;
            /* read the pointer to the name field (same access as the original code) */
            if (bus) {
                bus_name_ptr = (const void *) bus->name;
            }
            fprintf(logf, "ITER node=%p bus=%p bus->name=%p name=%p\n",
                    (void *) node, bus_ptr, bus_name_ptr, (const void *) name);
            fflush(logf);
        }

        /* Preserve original logic: call strcmp exactly as before. This may read past bounds
         * if either operand is not a valid NUL-terminated string — we deliberately keep
         * the same behavior so tests reproducing the violation will show the same outcome. */
        int cmp = strcmp(bus->name, name);

        if (logf) {
            fprintf(logf, "strcmp(bus->name=%p, name=%p) -> %d\n",
                    (const void *) (bus ? bus->name : NULL), (const void *) name, cmp);
            fflush(logf);
        }

        if (cmp == 0 && result) {
            if (logf) {
                fprintf(logf, "MATCH found: bus=%p -> storing to *result and returning 0\n",
                        (void *) bus);
                fclose(logf);
            }
            *result = bus;
            return 0;
        }
    }

    if (logf) {
        fprintf(logf, "END metal_bus_find: not found -> returning -ENOENT\n");
        fclose(logf);
    }
    return -ENOENT;
}
