int metal_bus_find(const char *name, struct metal_bus **result)
{
    struct metal_list *node;
    struct metal_bus *bus;
    /* Logging probe file. Keep as lightweight as possible. */
    FILE *log = NULL;

    /* Try to open the log file; if unavailable, continue without logging. */
    log = fopen("/tmp/logs/625.txt", "a");
    if (log) {
        fprintf(log, "ENTER metal_bus_find: name=%s, result=%p\n",
                name ? name : "(null)", (void *)result);
        fflush(log);
    }

    metal_list_for_each(&_metal.common.bus_list, node) {
        bus = metal_container_of(node, struct metal_bus, node);
        if (log) {
            fprintf(log, "  VISIT bus=%p, bus->name=%p, bus->name='%s'\n",
                    (void *)bus,
                    (void *)bus->name,
                    bus->name ? bus->name : "(null)");
            fflush(log);
        }

        if (strcmp(bus->name, name) == 0 && result) {
            if (log) {
                fprintf(log, "  MATCH found: assigning *result = %p (bus) and returning 0\n",
                        (void *)bus);
                fflush(log);
            }
            *result = bus;
            if (log) {
                fclose(log);
            }
            return 0;
        }
    }

    if (log) {
        fprintf(log, "EXIT metal_bus_find: no match found, returning -ENOENT\n");
        fclose(log);
    }
    return -ENOENT;
}
