int metal_bus_register(struct metal_bus *bus)
{
    /* Probe log to capture control flow and state for analysis. */
    FILE *logf = fopen("/tmp/logs/63.txt", "a");
    if (logf) {
        fprintf(logf, "metal_bus_register: entered, bus=%p\n", (void *)bus);
        if (bus) {
            fprintf(logf, "metal_bus_register: bus->name pointer=%p\n", (void *)bus->name);
        } else {
            fprintf(logf, "metal_bus_register: bus is NULL\n");
        }
        fclose(logf);
    }

    /* Preserve original validation logic exactly as in the target. */
    if (!bus || !bus->name || !strlen(bus->name)) {
        logf = fopen("/tmp/logs/63.txt", "a");
        if (logf) {
            if (!bus) {
                fprintf(logf, "metal_bus_register: returning -EINVAL because bus == NULL\n");
            } else if (!bus->name) {
                fprintf(logf, "metal_bus_register: returning -EINVAL because bus->name == NULL\n");
            } else {
                /* We called strlen(bus->name) here as in original; capture that event. */
                fprintf(logf, "metal_bus_register: returning -EINVAL because strlen(bus->name) == 0 (empty name)\n");
            }
            fclose(logf);
        }
        return -EINVAL;
    }

    /* Log just before calling metal_bus_find to help reproduce scenarios where that call
       may internally call strcmp or other string functions that can dereference the pointers. */
    logf = fopen("/tmp/logs/63.txt", "a");
    if (logf) {
        fprintf(logf, "metal_bus_register: about to call metal_bus_find with name pointer=%p\n", (void *)bus->name);
        fclose(logf);
    }

    if (metal_bus_find(bus->name, NULL) == 0) {
        logf = fopen("/tmp/logs/63.txt", "a");
        if (logf) {
            fprintf(logf, "metal_bus_register: metal_bus_find returned 0 -> bus exists; returning -EEXIST\n");
            fclose(logf);
        }
        return -EEXIST;
    }

    /* Continue with original registration logic. */
    metal_list_init(&bus->devices);
    metal_list_add_tail(&_metal.common.bus_list, &bus->node);
    metal_log(METAL_LOG_DEBUG, "registered %s bus\n", bus->name);

    logf = fopen("/tmp/logs/63.txt", "a");
    if (logf) {
        fprintf(logf, "metal_bus_register: registration complete for bus pointer=%p name_ptr=%p\n", (void *)bus, (void *)bus->name);
        fclose(logf);
    }

    return 0;
}
