int metal_bus_register(struct metal_bus *bus)
{
    FILE *f = fopen("/tmp/logs/65.txt", "a");
    if (f) {
        fprintf(f, "metal_bus_register entered: bus=%p\n", (void *)bus);
    }

    if (!bus) {
        if (f) {
            fprintf(f, "-> returning -EINVAL because bus is NULL\n");
            fclose(f);
        }
        return -EINVAL;
    }

    if (f) {
        fprintf(f, "bus->name ptr=%p\n", (void *)bus->name);
    }

    if (!bus->name) {
        if (f) {
            fprintf(f, "-> returning -EINVAL because bus->name is NULL\n");
            fclose(f);
        }
        return -EINVAL;
    }

    {
        size_t name_len = strlen(bus->name);
        if (f) {
            fprintf(f, "bus->name.len=%zu name=\"%s\"\n", name_len, bus->name);
        }
        if (name_len == 0) {
            if (f) {
                fprintf(f, "-> returning -EINVAL because bus->name is empty\n");
                fclose(f);
            }
            return -EINVAL;
        }
    }

    int find_res = metal_bus_find(bus->name, NULL);
    if (f) {
        fprintf(f, "metal_bus_find returned %d\n", find_res);
    }
    if (find_res == 0) {
        if (f) {
            fprintf(f, "-> returning -EEXIST because bus name already registered\n");
            fclose(f);
        }
        return -EEXIST;
    }

    metal_list_init(&bus->devices);
    if (f) {
        fprintf(f, "initialized devices list at %p\n", (void *)&bus->devices);
    }

    metal_list_add_tail(&_metal.common.bus_list, &bus->node);
    if (f) {
        fprintf(f, "added to global bus_list: node=%p\n", (void *)&bus->node);
    }

    metal_log(METAL_LOG_DEBUG, "registered %s bus\n", bus->name);

    if (f) {
        fprintf(f, "logged registration via metal_log and returning 0\n");
        fclose(f);
    }

    return 0;
}
