int metal_bus_register(struct metal_bus *bus)
{
    /* Open log file to record control flow and state for the violation scenario. */
    FILE *log = fopen("/tmp/logs/65.txt", "a");
    if (log)
    {
        fprintf(log, "enter metal_bus_register: bus=%p\n", (void *)bus);
        fflush(log);
    }

    if (!bus)
    {
        if (log)
        {
            fprintf(log, "exit metal_bus_register: return -EINVAL (bus is NULL)\n");
            fclose(log);
        }
        return -EINVAL;
    }

    if (log)
        fprintf(log, "bus pointer valid: bus=%p, bus->name=%p\n", (void *)bus, (void *)bus->name);

    if (!bus->name)
    {
        if (log)
        {
            fprintf(log, "exit metal_bus_register: return -EINVAL (bus->name is NULL)\n");
            fclose(log);
        }
        return -EINVAL;
    }

    /* Capture the length and contents of the name (this is the read that demonstrates
       the scenario where a non-const char * is only read and thus should be const-qualified). */
    size_t name_len = strlen(bus->name);
    if (log)
    {
        /* Print pointer, length and a copy of the string for evidence. */
        fprintf(log, "bus->name addr=%p name_len=%zu name=\"%s\"\n", (void *)bus->name, name_len, bus->name);
        fflush(log);
    }

    if (!name_len)
    {
        if (log)
        {
            fprintf(log, "exit metal_bus_register: return -EINVAL (name length is zero)\n");
            fclose(log);
        }
        return -EINVAL;
    }

    int found = metal_bus_find(bus->name, NULL);
    if (log)
    {
        fprintf(log, "metal_bus_find('%s') returned %d\n", bus->name, found);
        fflush(log);
    }

    if (found == 0)
    {
        if (log)
        {
            fprintf(log, "exit metal_bus_register: return -EEXIST (bus with same name exists)\n");
            fclose(log);
        }
        return -EEXIST;
    }

    metal_list_init(&bus->devices);
    if (log)
        fprintf(log, "metal_list_init(&bus->devices) done\n");

    metal_list_add_tail(&_metal.common.bus_list, &bus->node);
    if (log)
        fprintf(log, "metal_list_add_tail(&_metal.common.bus_list, &bus->node) done\n");

    metal_log(METAL_LOG_DEBUG, "registered %s bus\n", bus->name);

    if (log)
    {
        fprintf(log, "metal_log called, exiting metal_bus_register with 0\n");
        fclose(log);
    }

    return 0;
}
