int metal_bus_register(struct metal_bus *bus)
{
    FILE *logf = fopen("/tmp/logs/65.txt", "a");
    if (logf)
    {
        fprintf(logf, "enter metal_bus_register: bus=%p\n", (void *)bus);
    }

    if (!bus)
    {
        if (logf)
            fprintf(logf, "bus is NULL -> return -EINVAL\n");
        if (logf)
            fclose(logf);
        return -EINVAL;
    }

    if (logf)
    {
        fprintf(logf, "bus->name ptr=%p\n", (void *)bus->name);
        if (bus->name)
            fprintf(logf, "bus->name string=\"%s\", strlen=%zu\n",
                    bus->name, strlen(bus->name));
        else
            fprintf(logf, "bus->name is NULL\n");
    }

    if (!bus->name || !strlen(bus->name))
    {
        if (logf)
            fprintf(logf, "invalid name (NULL or empty) -> return -EINVAL\n");
        if (logf)
            fclose(logf);
        return -EINVAL;
    }

    if (logf)
        fprintf(logf, "about to call metal_bus_find(name=%p, ...) with name=\"%s\"\n",
                (void *)bus->name, bus->name);

    int found = metal_bus_find(bus->name, NULL);

    if (logf)
        fprintf(logf, "metal_bus_find returned %d\n", found);

    if (found == 0)
    {
        if (logf)
            fprintf(logf, "bus already exists -> return -EEXIST\n");
        if (logf)
            fclose(logf);
        return -EEXIST;
    }

    /* preserve original behavior */
    metal_list_init(&bus->devices);
    metal_list_add_tail(&_metal.common.bus_list, &bus->node);
    metal_log(METAL_LOG_DEBUG, "registered %s bus\n", bus->name);

    if (logf)
        fprintf(logf, "registered bus successfully -> return 0\n");
    if (logf)
        fclose(logf);

    return 0;
}
