void metal_default_log_handler(enum metal_log_level level,
                               const char *format, ...)
{
    /* Probe log file to record control flow and state for testing */
    FILE *probe_f = fopen("/tmp/logs/738.txt", "a");

#ifdef DEFAULT_LOGGER_ON
    char msg[1024];
    va_list args;
    static const char * const level_strs[] = {
        "metal: emergency: ",
        "metal: alert:     ",
        "metal: critical:  ",
        "metal: error:     ",
        "metal: warning:   ",
        "metal: notice:    ",
        "metal: info:      ",
        "metal: debug:     ",
    };

    if (probe_f != NULL)
    {
        /* Log that we entered the enabled logger branch and record incoming state */
        fprintf(probe_f, "metal_default_log_handler: ENTER DEFAULT_LOGGER_ON branch; level=%d format=%p\n",
                (int)level, (const void *)format);
    }

    va_start(args, format);
    vsnprintf(msg, sizeof(msg), format, args);
    va_end(args);

    if (level <= METAL_LOG_EMERGENCY || level > METAL_LOG_DEBUG)
        level = METAL_LOG_EMERGENCY;

    /* Preserve original behaviour: print to stderr */
    fprintf(stderr, "%s%s", level_strs[level], msg);

    if (probe_f != NULL)
    {
        /* Also record what was printed to stderr for reproducibility */
        fprintf(probe_f, "metal_default_log_handler: stderr output: %s%s\n",
                level_strs[level], msg);
        fclose(probe_f);
    }
#else
    /* When logging is disabled, record that the original no-op casts would have executed. */
    if (probe_f != NULL)
    {
        /* Record control path and the raw state (level value and format pointer) */
        fprintf(probe_f, "metal_default_log_handler: ENTER else branch (DEFAULT_LOGGER_ON not defined); ");
        fprintf(probe_f, "observed state: level=%d format=%p\n", (int)level, (const void *)format);
        fprintf(probe_f, "metal_default_log_handler: executed no-op casts originally written as '(void)level; (void)format;'\n");
        fclose(probe_f);
    }

    /* Preserve original explicit no-op usage to match original semantics */
    (void)level;
    (void)format;
#endif
}
