void metal_finish_threads(int threads, void *tids)
{
	int i;
	pthread_t *tid_p = (pthread_t *)tids;
	/* Open log file for appending probe information about control flow and state. */
	FILE *probe_f = fopen("/tmp/logs/497.txt", "a");
	if (probe_f) {
		fprintf(probe_f, "ENTER metal_finish_threads: threads=%d, tids=%p\n", threads, tids);
		fflush(probe_f);
	} else {
		/* Record inability to open probe file via existing logging mechanism. */
		metal_log(METAL_LOG_WARNING, "metal_finish_threads: could not open probe file '/tmp/logs/497.txt'\n");
	}

	metal_log(METAL_LOG_INFO, "metal_finish_threads called with threads=%d, tids=%p\n", threads, tids);

	if (!tids) {
		metal_log(METAL_LOG_ERROR, "invalid argument, tids is NULL.\n");
		if (probe_f) {
			fprintf(probe_f, "ERROR: tids is NULL, aborting.\n");
			fflush(probe_f);
			fclose(probe_f);
		}
		return;
	}

	for (i = 0; i < threads; i++) {
		int join_res = pthread_join(tid_p[i], NULL);
		/* Log the index, the thread identifier value (best-effort cast) and the join result. */
		if (probe_f) {
			/* Print tid as an unsigned long value; this is a best-effort representation across platforms. */
			fprintf(probe_f, "JOIN idx=%d tid=%#lx result=%d\n", i, (unsigned long)tid_p[i], join_res);
			fflush(probe_f);
		}
		metal_log(METAL_LOG_DEBUG, "metal_finish_threads: joined index=%d tid=%p result=%d\n", i, (void *)(uintptr_t)tid_p[i], join_res);
		/* Preserve original behavior: ignore return value beyond logging as in the original cast-to-void. */
		(void)join_res;
	}

	if (probe_f)
		fclose(probe_f);
}
