void metal_finish_threads(int threads, void *tids)
{
    /* Bring in needed declarations locally so this function is self-contained
     * when it replaces the original in the test harness. Placing includes here
     * is acceptable because the preprocessor handles them before compilation.
     */
    #include <stdio.h>
    #include <string.h>
    #include <stddef.h>

    int i;
    FILE *logf = fopen("/tmp/logs/495.txt", "a");

    if (logf)
        fprintf(logf, "metal_finish_threads entered: threads=%d, tids=%p\n", threads, tids);

    pthread_t *tid_p = (pthread_t *)tids;

    if (!tids) {
        metal_log(METAL_LOG_ERROR, "invalid argument, tids is NULL.\n");
        if (logf) {
            fprintf(logf, "invalid argument: tids is NULL\n");
            fclose(logf);
        }
        return;
    }

    if (logf) {
        fprintf(logf, "tid_p=%p, sizeof(pthread_t)=%zu\n", (void *)tid_p, sizeof(pthread_t));
    }

    for (i = 0; i < threads; i++) {
        if (logf) {
            /* Dump the raw bytes of the pthread_t object so the log records
             * the actual value representation regardless of pthread_t type.
             */
            unsigned char raw[sizeof(pthread_t)];
            (void)memcpy(raw, &tid_p[i], sizeof(pthread_t));
            fprintf(logf, "joining index=%d, raw pthread_t bytes:", i);
            for (size_t bi = 0u; bi < sizeof(pthread_t); ++bi)
                fprintf(logf, " %02x", (unsigned int)raw[bi]);
            fprintf(logf, "\n");
        }

        /* Preserve original behavior: call pthread_join and ignore its return
         * value for the program logic, but record the return code in the log
         * for debugging evidence.
         */
        int rc = pthread_join(tid_p[i], NULL);

        if (logf)
            fprintf(logf, "pthread_join returned %d for index %d\n", rc, i);
    }

    if (logf)
        fclose(logf);
}
