void metal_finish_threads(int threads, void *tids)
{
    int i;
    /* Replicate original (non-const) cast to demonstrate the reported MISRA violation. */
    pthread_t *tid_p = (pthread_t *)tids;

    /* Attempt to open a persistent log so other agents can reproduce the control flow and state. */
    FILE *log = fopen("/tmp/logs/495.txt", "a");

    if (log) {
        fprintf(log, "ENTER: metal_finish_threads called\n");
        fprintf(log, "  threads=%d\n", threads);
        fprintf(log, "  tids pointer (raw)=%p\n", tids);

        if (!tids) {
            fprintf(log, "  ERROR: tids is NULL -> calling metal_log and returning\n");
            fflush(log);
            fclose(log);

            /* Preserve original behavior */
            metal_log(METAL_LOG_ERROR, "invalid argument, tids is NULL.\n");
            return;
        }

        fprintf(log, "  NOTE: casting to non-const pthread_t * (pthread_t *tid_p = (pthread_t *)tids)\n");
        fprintf(log, "  This shows the pointer is not const-qualified even though elements are only read.\n");

        for (i = 0; i < threads; i++) {
            /* Log the value read from tid_p[i]. Use an integer cast for visibility. */
            unsigned long tid_val = (unsigned long)tid_p[i];
            fprintf(log, "  Iteration %d: tid_p[%d] raw_value=(unsigned long)%lu\n", i, i, tid_val);
            fflush(log);

            /* Preserve original join behavior. */
            (void)pthread_join(tid_p[i], NULL);

            fprintf(log, "  Iteration %d: pthread_join completed for index %d\n", i, i);
            fflush(log);
        }

        fprintf(log, "EXIT: metal_finish_threads completed normally\n");
        fflush(log);
        fclose(log);
    } else {
        /* If the log file cannot be opened, still preserve original behavior. */
        if (!tids) {
            metal_log(METAL_LOG_ERROR, "invalid argument, tids is NULL.\n");
            return;
        }

        for (i = 0; i < threads; i++)
            (void)pthread_join(tid_p[i], NULL);
    }
}
