static int metal_init_page_sizes(void)
{
    const int max_sizes = MAX_PAGE_SIZES - 1;
    long sizes[max_sizes];
    FILE *f = fopen("/tmp/logs/658.txt", "a");

    /* Minimal entry probe so tests show the function was entered. */
    if (f) {
        fprintf(f, "ENTER metal_init_page_sizes\n");
        fflush(f);
    }

    /* Determine system page size. */
    sizes[0] = getpagesize();
    if (f) {
        fprintf(f, "getpagesize() -> %ld\n", sizes[0]);
        fflush(f);
    }
    if (sizes[0] <= 0) {
        metal_log(METAL_LOG_ERROR, "failed to get page size\n");
        if (f) {
            fprintf(f, "failed to get page size, returning -EINVAL\n");
            fclose(f);
        }
        return -EINVAL;
    }
    _metal.page_size  = sizes[0];
    _metal.page_shift = metal_log2(sizes[0]);
    if (f) {
        fprintf(f, "system page_size=%ld page_shift=%d _metal.tmp_path=%s\n",
                _metal.page_size, _metal.page_shift,
                (_metal.tmp_path ? _metal.tmp_path : "(null)"));
        fflush(f);
    }

    metal_add_page_size(_metal.tmp_path, _metal.page_shift, 0);
    if (f) {
        fprintf(f, "called metal_add_page_size(tmp_path, %d, 0)\n", _metal.page_shift);
        fflush(f);
    }

#ifdef HAVE_HUGETLBFS_H
#ifndef MAP_HUGE_SHIFT
    /* System does not support multiple huge page sizes. */
    sizes[0] = gethugepagesize();
    if (f) {
        fprintf(f, "(no MAP_HUGE_SHIFT) gethugepagesize() -> %ld\n", sizes[0]);
        fflush(f);
    }
    if (sizes[0] > 0) {
        metal_add_page_size(hugetlbfs_find_path(),
                    metal_log2(sizes[0]),
                    MAP_HUGETLB);
        if (f) {
            fprintf(f, "added single huge page size %ld (shift %d)\n",
                    sizes[0], metal_log2(sizes[0]));
            fflush(f);
        }
    }
#else
    if (gethugepagesize() >= 0) {
        int i, count;

        /* System supports multiple huge page sizes. */
        count = gethugepagesizes(sizes, max_sizes);
        if (f) {
            fprintf(f, "(MAP_HUGE_SHIFT) gethugepagesizes returned count=%d\n", count);
            fflush(f);
        }
        for (i = 0; i < count; i++) {
            int shift = metal_log2(sizes[i]);

            if (f) {
                fprintf(f, "huge size[%d]=%ld shift=%d\n", i, sizes[i], shift);
                fflush(f);
            }

            if ((shift & MAP_HUGE_MASK) != shift)
                continue;
            metal_add_page_size(
                hugetlbfs_find_path_for_size(sizes[i]),
                shift, (MAP_HUGETLB |
                (shift << MAP_HUGE_SHIFT)));
            if (f) {
                fprintf(f, "called metal_add_page_size for huge size %ld (shift %d, flags 0x%x)\n",
                        sizes[i], shift,
                        (int)(MAP_HUGETLB | (shift << MAP_HUGE_SHIFT)));
                fflush(f);
            }
        }
    } else {
        if (f) {
            fprintf(f, "gethugepagesize() < 0, no huge pages available\n");
            fflush(f);
        }
    }
#endif
#endif

    /* Finally sort the resulting array by size. */
    if (f) {
        fprintf(f, "_metal.num_page_sizes before qsort = %d\n", _metal.num_page_sizes);
        fprintf(f, "_metal.page_sizes pointer = %p\n", (void *)_metal.page_sizes);
        /* Log entries count; avoid dereferencing unknown struct layout. */
        fflush(f);
    }

    /* Probe: log that qsort is about to be invoked (this is the call under test). */
    if (f) {
        fprintf(f, "invoking qsort on %d elements\n", _metal.num_page_sizes);
        fflush(f);
    }

    qsort(_metal.page_sizes, _metal.num_page_sizes,
          sizeof(struct metal_page_size), metal_pagesize_compare);

    if (f) {
        fprintf(f, "qsort returned; _metal.num_page_sizes after qsort = %d\n", _metal.num_page_sizes);
        fprintf(f, "EXIT metal_init_page_sizes\n");
        fflush(f);
        fclose(f);
    }

    return 0;
}
